/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {	
	for(var i=0;i<dsBlngCertList.rowcount;i++){
		if (dsBlngCertList.GetColumn(i, "chk") == "1"){ 
			if (dsBlngCertList.GetColumn(i, "PRINT_YN") == "Y"){
				fnc_Message("인쇄한 증명서는 삭제할 수 없습니다.", "N");
			}
			else
			{
				//DataSet의 Header 정보가 있을 때  Add 처리 	
				if ( !fnc_DeleteCheck(dsBlngCertList, "chk", "1") ) return;          
				dsBlngCertList_OnRowPosChanged();   
			}
		}
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  Column의 값이 변경된 경우
+-------------------------------------------------------------------------------------------------*/
function dsBlngCertList_OnRowPosChanged(obj,nOldRow,nRow)
{
    var sArg = "";
    if ( dsBlngCertList.GetRowCount() == 0 ) return;
    
    if ( dsBlngCertList.GetRowCount() <= dsBlngCertList.currow ) {
        dsBlngCertList.FireEvent = false;
        dsBlngCertList.row = dsBlngCertList.GetRowCount() - 1;
        dsBlngCertList.SelectRow(dsBlngCertList.GetRowCount() - 1);
        dsBlngCertList.FireEvent = true;
    }

	if ( fnc_DatasetChangeCheck(dsBlngCertList) ) {
		if ( fnc_Message(SNIS_COM_1002, "Y") ) {
            dsBlngCertList.FireEvent = false;
            dsBlngCertList.row = nOldRow;
			fcSave();
            dsBlngCertList.FireEvent = true;
			return;
		}
	}
	
    //fcSearch();
}

