function fcSelectRacer(){
	
	if(grdBlngCert.GetClickedCell() == 4){
		var strCandNo = grdBlngCert.GetCellValue(grdBlngCert.ClickedRow, 4);
		for(var i=0;i<dsRacerList.rowcount;i++){
			if(dsRacerList.GetColumn(i, "NM_KOR") == strCandNo){
				dsBlngCertList.SetColumn(dsBlngCertList.currow, "CAND_NO", dsRacerList.GetColumn(i, "CAND_NO"));
			}
		}
	}
}


