/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
   
    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {
    
        dsOutAbsList.ClearData();
    }    
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {

	fcInit(obj);
	
	if( fnc_IsNull(cbPerioNo.Value) ) {
        // 기수 누락
        fnc_Message(SNIS_CAN_0008, "N", "기수");
        return false;
    }
   
    if( fnc_IsNull(cbYear.Value) ) {       
        // 연도 누락
        fnc_Message(SNIS_CAN_0008, "N", "년도");
        return false;
    }
    
    if( fnc_IsNull(cbMonth.Value) ) {
    
        // 월 누락
        fnc_Message(SNIS_CAN_0008, "N", "월");
        return false;
    }
         		
	return true;
}

//  월 일일 결석사항 조회
function fcSearchAbsList() {

    var sServiceName  = "d02000015-service:searchEduAbsList";
    var sInDataSet    = "";
    var sOutDataSet   = " dsAbsList=dsAbsList ";
        sOutDataSet  += " dsEduTime=dsEduTime ";
           
    // 월 코드값을 두자리 월단위로 가공
	var mm = ToString(ToInteger(SubStr(cbMonth.Value, 1, 2)));
	if(Bound(mm) == 1) {
        mm = "0"+ToString(mm);
	}
			
	// 년과 월을 합성.
	var yearMonth = Trim(cbYear.Value)+Trim(mm);
	
	fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));	
	fcd_AddParam("yearMonth", yearMonth);
			
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;
    
    dsOutAbsList.ClearData();
    dsAbsList.ClearData();
    dsEduTime.ClearData();
      
    fcSearchAbsList();	        
}

