/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
    switch(sServiceName) {
    
        // 콤보 조회후
        case "d02000015-service:searchPerioNo" :
            cbPerioNo.Index = 0;
            cbYear.Index = 0;
            cbMonth.Index = 0;
            break;
      
        
        // 결석사항 조회후
        case "d02000015-service:searchEduAbsList" :
                        
            fcTransferDataSet();    
            break;               
                       
        default :
            break;
    }
    
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

// 기본교육시간을 출력
function fcTransferDataSet() {
    
    var numberVal = 0;
    var countVal = 0;
    var eduTime = 0;
    //  일인당 기본데이터 카운트 구하기
    if( dsEduTime.rowcount >0 ) {
        
        
        for(var i=0; i<dsEduTime.rowcount; i++) {
                
            if(dsEduTime.GetColumn(0, "CAND_NO") == dsEduTime.GetColumn(i, "CAND_NO")) {
                
                numberVal ++;
            }    
        }
     }      
        // 대상자 카운트 구하기
     countVal = (dsEduTime.rowcount) / numberVal;
           
     dsOutAbsList.ClearData();
     
     // 대상자 카운트만큼 후보생 번호, 이름 출력 
     if(countVal > 0) {
         var k=0;    
         for(var i=0; i<countVal; i++) {
                
            // 대상자 수만큼 row 생성
            dsOutAbsList.AddRow();
            dsOutAbsList.SetColumn(i, "CAND_NO", dsEduTime.GetColumn(k, "CAND_NO"));
            dsOutAbsList.SetColumn(i, "NM_KOR", dsEduTime.GetColumn(k, "NM_KOR"));
              
            k = k + numberVal;
         }   
      }      
      
      //  교육훈련일지 참석시간 오전/오후별 설정
      for(var j=0; j<dsEduTime.rowcount; j++) {
                       
          var myDay = Trim(SubStr(dsEduTime.GetColumn(j, "DT"),6,2)); 
          var candNo = Trim(dsEduTime.GetColumn(j, "CAND_NO"));                                                                           
         // var eduTime = 0;
          
          for(var s=0; s<dsOutAbsList.rowcount; s++) {
                                
              if(Trim(dsOutAbsList.GetColumn(s, "CAND_NO")) == candNo) {
                  // 시작 시각 추출               
                  var ampmVal = ToInteger(SubStr(dsEduTime.GetColumn(j, "STR_END_TIME"), 0, 2));
               
                  // 오전
                  if((ampmVal >= 9) && (ampmVal <= 12)) {
                      dsOutAbsList.SetColumn(s, "D"+myDay+"AM", dsEduTime.GetColumn(j,"EDU_TIME"));  
                   //   eduTime = eduTime + ToInteger(dsEduTime.GetColumn(j,"EDU_TIME")); 
                  }
                  // 오후
                  else if((ampmVal >= 13) && (ampmVal <= 18)) {
                      dsOutAbsList.SetColumn(s, "D"+myDay+"PM", dsEduTime.GetColumn(j,"EDU_TIME"));
                  //    eduTime = eduTime + ToInteger(dsEduTime.GetColumn(j,"EDU_TIME")); 
                  }
                  else {
                      fnc_Message(SNIS_CAN_M004, "N");
                  }           
              }
          }
        //  dsOutAbsList.SetColumn(j, "TOT_TIME", eduTime);     
      }
            
      //  교육훈련일지 결석사유 오전/오후별 설정            
      for(var j=0; j<dsAbsList.rowcount; j++) {
         
          var myDay = Trim(SubStr(dsAbsList.GetColumn(j, "DT"),6,2)); 
          var candNo = Trim(dsAbsList.GetColumn(j, "CAND_NO")); 
             
          for(var s=0; s<dsOutAbsList.rowcount; s++) {
                                
              if(Trim(dsOutAbsList.GetColumn(s, "CAND_NO")) == candNo) {
                  
                  // 시작 시각 추출               
                  var ampmVal = ToInteger(SubStr(dsAbsList.GetColumn(j, "STR_END_TIME"), 0, 2));
                  // 오전
                  if((ampmVal >= 9) && (ampmVal <= 12)) {
                      dsOutAbsList.SetColumn(s, "D"+myDay+"AM", dsAbsList.GetColumn(j,"RMK"));  
                  }
                  // 오후
                  else if((ampmVal >= 13) && (ampmVal <= 18)) {
                      dsOutAbsList.SetColumn(s, "D"+myDay+"PM", dsAbsList.GetColumn(j,"RMK"));
                  }
                  else {
                      fnc_Message(SNIS_CAN_M004, "N");
                  }
                                  
              }
          }       
      }
      
      for(var s=0; s<dsOutAbsList.rowcount; s++) {
            for(var j=0; j<dsEduTime.rowcount; j++) {
                 if(Trim(dsOutAbsList.GetColumn(s, "CAND_NO")) == Trim(dsEduTime.GetColumn(j, "CAND_NO"))) {
					 eduTime = eduTime + ToInteger(dsEduTime.GetColumn(j,"EDU_TIME")); 
                 }
            }
            for(var j=0; j<dsAbsList.rowcount; j++) {
                 if(Trim(dsOutAbsList.GetColumn(s, "CAND_NO")) == Trim(dsAbsList.GetColumn(j, "CAND_NO"))) {
					 eduTime = eduTime - ToInteger(dsAbsList.GetColumn(j,"EDU_TIME")); 
                 }
            }
            dsOutAbsList.SetColumn(s, "TOT_TIME", eduTime); 
            eduTime =0;
      }
}


