// Grid Popup 
function fcPopWin(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var str = Trim(ToString(SubStr(cbMonth.value,1, 2)));
	var strTwo = Trim(SubStr(dsOutAbsList.GetColID(grdAbsList.CurrentCell-2), 1, 2));
    var strThree = Trim(SubStr(dsOutAbsList.GetColID(grdAbsList.CurrentCell-2), 3, 2));
    
	
	switch(grdAbsList.CurrentCell) {
        // GRID 에서 일자 범위내 경우가 아닐 경우 팝업 막기 
        case "0" :
        case "1" :
        case "2" :
            break;
        // GRID 에서 일자 범위내 경우일때 팝업 처리    
        default  :
 
            if(Bound(str) == 1) {  
                str = "0" + str;
            }
            
            var selDate    = Trim(cbYear.Value)+"-"+str+"-"+strTwo;
            var selAmpmGbn = strThree;
            var selNm      = grdAbsList.GetCellValue(grdAbsList.CurrentRow, 2); 
            var selRmk     = grdAbsList.GetCellValue(grdAbsList.CurrentRow, grdAbsList.CurrentCell);
                    
            // Content로 넘겨지는 arguements
            var sArg   = "selDate=" + quote(selDate);
                sArg  += " selAmpmGbn=" + quote(selAmpmGbn);
                sArg  += " selNm=" + quote(selNm);
                sArg  += " selrmk=" + quote(selRmk);
            
            if(Bound(selRmk) > 1) {
                Dialog("common::comAbsPopUp.xml", sArg, 250, 250);
            }
            break;
     }        
}


