/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
	switch(sServiceName) {
        
        case "d02000016-service:searchPerioNo" :	
			//fcPerioNoCount();
			break;
        // 기수카운터 조회
        case "d02000016-service:searchPerioNoCount" :	
			iGTotPersNo = toNumber(dsPerioNoCount.GetColumn(0,"P_COUNT"));
			iGAbsPersNo = toNumber(dsAbsCount.GetColumn(0,"A_COUNT"));
			iGCurrPersNo = iGTotPersNo - iGAbsPersNo;
			
			edTotPersNo.value 	= iGTotPersNo;			
			edCurrPersNo.value	= iGCurrPersNo;
			edAbsPersNo.value 	= iGAbsPersNo;
			break;			
        // 일일교육훈련일지 마스터
        case "d02000016-service:searchDiaryMst" : 
			//fcAbsCount();
            break;
        // 일일교육훈련일지 마스터new
        case "d02000016-service:searchDiaryMst1" :
            if (fnc_IsNull(dsDiaryMst.GetColumn(0,"TOT_PERS_NO"))){ // 저장된 일일교육훈련일지 없는경우
				fcPerioNoCount();
            }
            fnc_DatasetPrint(dsDiaryAbsReport);
            
            if((dsCfm.GetColumn(0,"CFG_YN")) == "N"){
                btnCfm.Enable = true;
            }
            else{
				btnCfm.Enable = false;
            }
            break; 
        case "d02000016-service:saveCfm" : 
			fcSearch();
			break;
		default :
			break;
	}
	
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

