>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
   
    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {
    
        dsCycAssem.ClearData();
       
    }    
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {

	fcInit(obj);
	
	if( fnc_IsNull(cbRacerPerioNo.Value) ) {
        // 기수 누락
        fnc_Message(SNIS_CAN_0008, "N", "기수");
        return false;
    }
    if( fnc_IsNull(cbGbn.Value) ) {
        // 기수 누락
        fnc_Message(SNIS_CAN_0008, "N", "구분");
        return false;
    }
   
	return true;
}


function fcSearch(obj) {
   
    if ( !fcSearchCheck(obj) ) return;
    
    // 중간/기말 환산기준 조회
    var sServiceName  = "d02000017-service:searchCycAssemBas";
    var sInDataSet    = "";
    var sOutDataSet   = " dsCycAssemBas=dsCycAssemBas ";
  
    fcd_AddParam("racerPerioNo", cbRacerPerioNo.value); 
    fcd_AddParam("examGbn", cbGbn.value);   
    
  	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);        
}

/*
    EXAM_CD     : D10007(시험구분) 
                - 012 (중간)
                - 013 (기말)
    ITEM_GBN_CD : D10027(종목구분)
                - 002 (실기)
    ITEM_CD     : D10064()            
        
    D10064 : 실기 
        - 201 : 자전거분해조립
        - 202 : 조립순서,공구사용 미숙 
*/

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {
	 
	//변경된 자료가 있는지 체크
    if ( !fnc_DatasetChangeCheck(dsCycAssem) )
    {
        //저장 할 자료가 없음
        fnc_Message(SNIS_COM_1006, "N");	
        return false;
    }
    if (fnc_IsNull(calDt.Value)){
		fnc_Message(SNIS_COM_1035, "N", "평가일자는");
        return false;
    }
    // 기수 체크
    switch(cbRacerPerioNo.Text) {
        
        case null :
        case ""   :
        case " "  :
        case "  " :
                    // 기수 입력항목 체크
                    fnc_Message(SNIS_COM_1035, "N", "기수 는");
                    return;
            break;
        default   :
                        
            break;
    }
		
	oDataSet = null;
	sMandatoryColID   = null;
	sMandatoryColName = null;
	oGrid             = null;
	
	oDataSet = dsCycAssem;
	sMandatoryColID   = "CAND_NO|NM_KOR|ASSEM_TIME|TOT_TIME|TOT_SCR";
	sMandatoryColName = "학번|성명|분해조립시간|총시간|총점";
	oGrid             = grdCycleAssem;
	
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
					
	return true;
}


function fcSave(obj) {
  
   if ( !fcSaveCheck() ) return;
    
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
       
    sServiceName     = "d02000017-service:saveCycAssem";
    
    sInDataSet       = "dsCycAssem=dsCycAssem:u" ;
    sOutDataSet      = "dsCycAssem=dsCycAssem" ;
   
    for(var i=0; i<dsCycAssem.rowcount; i++) {
        
       dsCycAssem.SetColumn(i, "RACER_PERIO_NO", Trim(cbRacerPerioNo.Value));
       dsCycAssem.SetColumn(i, "EXAM_CD", "003");
       dsCycAssem.SetColumn(i, "ITEM_GBN_CD", "002");
       dsCycAssem.SetColumn(i, "DT", calDt.Value);
       dsCycAssem.SetColumn(i, "ASSEM_TIME", dsCycAssem.GetColumn(i,"ASSEM_TIME"));
       dsCycAssem.SetColumn(i, "ADD_TIME", dsCycAssem.GetColumn(i,"ADD_TIME"));
    }
    
     fcd_AddParam("racerPerioNo", Trim(cbRacerPerioNo.Value));  
	 fcd_AddParam("dT", calDt.Value); 
	 fcd_AddParam("candNo", dsCycAssem.GetColumn(0,"CAND_NO")); 
     fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

