/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
    switch(sServiceName) {
    
        // 공통코드 조회시
        case "d02000017-service:searchPerioNo" :
                       
            break;
                 
         // 자전거 분해조립 환산기준 조회했을 경우
        case  "d02000017-service:searchCycAssemBas" :          
            break;
        
        case "d02000017-service:saveCycAssem"   :
             fnc_DataSetPrint(dsCycAssem);          
            break;
          
         case "d02000017-service:searchCycleAssem" :             
                           
            break;   
                               
        default :
            break;
    }
    
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

