function dsCycAssem_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
    var bas100from_scr	= 0.0;
	var bas100to_scr	= 0.0;
	var bas100_scr		= 0.0;
	var bas90from_scr	= 0.0;
	var bas90to_scr		= 0.0;
	var bas90_scr		= 0.0;
	var bas80from_scr	= 0.0;
	var bas80to_scr		= 0.0;
	var bas80_scr		= 0.0;
	var bas70from_scr	= 0.0;
	var bas70to_scr		= 0.0;
	var bas70_scr		= 0.0;
	var bas60from_scr	= 0.0;
	var bas60to_scr		= 0.0;
	var bas60_scr		= 0.0;
	var bas50from_scr	= 0.0;
	var bas50to_scr		= 0.0;
	var bas50_scr		= 0.0;
	var bas0from_scr	= 0.0;
	var bas0to_scr		= 0.0;
	var bas0_scr		= 0.0;
	
	var scrName 		= ""; //종목
	var scrValue 		= ""; //종목점수
	
	varNewValue = toNumber(varNewValue); //실수형으로 변환해야함	
   
    for(var i=0; i<dsCycAssem.rowcount; i++) {  
			
		if(i == nRow) { 
            if(Trim(strColumnID) == "ASSEM_TIME") {
               
				for(var j=0; j<dsExamChng.rowcount; j++) {
                       
                        bas100from_scr	= toNumber(dsExamChng.GetColumn(j,"BAS100FROM_SCR"));
                        bas100to_scr	= toNumber(dsExamChng.GetColumn(j,"BAS100TO_SCR"));
                        bas100_scr		= toNumber(dsExamChng.GetColumn(j,"BAS100_SCR"));
                        bas90from_scr	= toNumber(dsExamChng.GetColumn(j,"BAS90FROM_SCR"));
                        bas90to_scr		= toNumber(dsExamChng.GetColumn(j,"BAS90TO_SCR"));
                        bas90_scr		= toNumber(dsExamChng.GetColumn(j,"BAS90_SCR"));
                        bas80from_scr	= toNumber(dsExamChng.GetColumn(j,"BAS80FROM_SCR"));
                        bas80to_scr		= toNumber(dsExamChng.GetColumn(j,"BAS80TO_SCR"));
                        bas80_scr		= toNumber(dsExamChng.GetColumn(j,"BAS80_SCR"));
                        bas70from_scr	= toNumber(dsExamChng.GetColumn(j,"BAS70FROM_SCR"));
                        bas70to_scr		= toNumber(dsExamChng.GetColumn(j,"BAS70TO_SCR"));
                        bas70_scr		= toNumber(dsExamChng.GetColumn(j,"BAS70_SCR"));
                        bas60from_scr	= toNumber(dsExamChng.GetColumn(j,"BAS60FROM_SCR"));
                        bas60to_scr		= toNumber(dsExamChng.GetColumn(j,"BAS60TO_SCR"));
                        bas60_scr		= toNumber(dsExamChng.GetColumn(j,"BAS60_SCR"));
                        bas50from_scr	= toNumber(dsExamChng.GetColumn(j,"BAS50FROM_SCR"));
                        bas50to_scr		= toNumber(dsExamChng.GetColumn(j,"BAS50TO_SCR"));
                        bas50_scr		= toNumber(dsExamChng.GetColumn(j,"BAS50_SCR"));
                        bas40from_scr	= toNumber(dsExamChng.GetColumn(j,"BAS40FROM_SCR"));
                        bas40to_scr		= toNumber(dsExamChng.GetColumn(j,"BAS40TO_SCR"));
                        bas40_scr		= toNumber(dsExamChng.GetColumn(j,"BAS40_SCR"));
                                                                              
                        if((bas100from_scr <= varNewValue) && (bas100to_scr >= varNewValue) ){
                            scrValue = bas100_scr;
                        }else if((bas90from_scr <= varNewValue) && (bas90to_scr >= varNewValue) ){
                            scrValue = bas90_scr;
                        }else if((bas80from_scr <= varNewValue) && (bas80to_scr >= varNewValue) ){
                            scrValue = bas80_scr;
                        }else if((bas70from_scr <= varNewValue) && (bas70to_scr >= varNewValue) ){
                            scrValue = bas70_scr;													
                        }else if((bas60from_scr <= varNewValue) && (bas60to_scr >= varNewValue) ){
                            scrValue = bas60_scr;	
                        }else if((bas50from_scr <= varNewValue) && (bas50to_scr >= varNewValue) ){
                            scrValue = bas50_scr;
                        }else if((bas40from_scr <= varNewValue) && (bas40to_scr >= varNewValue) ){
                            scrValue = bas40_scr;
                        }else{
                            scrValue = 0;
                        }
                      
                        break;					
				}
			
				dsCycAssem.SetColumn(i, "TOT_SCR", scrValue);
				dsCycAssem.SetColumn(i, "TOT_TIME", dsCycAssem.GetColumn(i,"ASSEM_TIME"));
			}
			else if(Trim(strColumnID) == "ADD_TIME") {
                var minVal      = 0;    // 분
                var secVal      = 0;    // 초
                var mileVal     = 0;    // 밀리초(1/100)
                var addSecVal   = 0;    // 분해미숙 추가 초
                var sumMileVal  = 0;    // 합산 밀리초
                
                var strMinVal   = null;  // 분(문자)
                var strSecVal   = null;  // 초(문자)
                var strMileVal  = null;  // 밀리초(문자)
                var strSumMileVal = null; // 합산 기록(문자)
                
                addSecVal = dsCycAssem.GetColumn(i, "ADD_TIME");  
                                
                sumMileVal = ( (toInteger(SubStr(dsCycAssem.GetColumn(i,"ASSEM_TIME"), 0, 2))*60*100)
                              +(toInteger(SubStr(dsCycAssem.GetColumn(i,"ASSEM_TIME"), 2, 2))*100)
                              +(toInteger(SubStr(dsCycAssem.GetColumn(i,"ASSEM_TIME"), 4, 2)))
                              +(toInteger(SubStr(addSecVal, 0, 2))*60*100)
                              +(toInteger(SubStr(addSecVal, 2, 2))*100)
                              +(toInteger(SubStr(addSecVal, 4, 2)))
                             );  // 합산 밀리초
            
                minVal  = toInteger(sumMileVal/(100*60));         // 분
                secVal  = toInteger((sumMileVal%(100*60))/100);   // 초 
                mileVal = toInteger((sumMileVal%(100*60))%100);   // 밀리초
                
                strMinVal = toString(minVal);
                strSecVal = toString(secVal);
                strMileVal= toString(mileVal);
                
                if(bound(strMinVal) == 1)  { strMinVal  = ("0"+strMinVal);  }
                if(bound(strSecVal) == 1)  { strSecVal  = ("0"+strSecVal);  }
                if(bound(strMileVal) == 1) { strMileVal = ("0"+strMileVal); }
                strSumMileVal = (strMinVal + strSecVal + strMileVal);
             
         //       alert(strSumMileVal);
                dsCycAssem.SetColumn(i, "TOT_TIME", strSumMileVal);
            }
		}			
	}
}

