/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
   
   if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
   switch(sServiceName) {
	   case "d02000019-service:searchPerioNo" :
				cbPerioNo.Index = 0;
				cbRound.Index = 0;
				cbBeforeRound.Index = 0;
				cbCurrentRound.Index = 0;
       case "d02000019-service:searchPerioExam1" :
            //    calDate.Value = null;   
            //fnc_DatasetPrint(dsPerioExam2);
            fcSetAvg();  
            break;
       case "d02000019-service:searchPerioExam2":
            //    calDate.Value = null;
            break;
           
       case "d02000019-service:searchPerioExam3":
           //     calDate.Value = null;
            //    fcSetAvg();   
            break;     
       
       case "d02000019-service:saveGetStrength" :
                    
            break;
       default :
       
           break;
   }
    
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

