    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false); 
}

// 평균 설정
function fcSetAvg() {
    if(dsPerioExam3.rowcount > 0) {
        
        for(var i=0; i<dsPerioExam3.rowcount; i++) {
            var cnt = 0;
            var avg = 0.0;
            
            var GRIP_LEFT_1    = dsPerioExam3.GetColumn(i,"GRIP_LEFT_1");
            var GRIP_LEFT_2    = dsPerioExam3.GetColumn(i,"GRIP_LEFT_2");
            var GRIP_LEFT_3    = dsPerioExam3.GetColumn(i,"GRIP_LEFT_3");
                        
            var GRIP_RIGHT_1    = dsPerioExam3.GetColumn(i,"GRIP_RIGHT_1");
            var GRIP_RIGHT_2    = dsPerioExam3.GetColumn(i,"GRIP_RIGHT_2");
            var GRIP_RIGHT_3    = dsPerioExam3.GetColumn(i,"GRIP_RIGHT_3");
                        
            var LEG_LEFT_1  = dsPerioExam3.GetColumn(i,"LEG_LEFT_1");
            var LEG_LEFT_2  = dsPerioExam3.GetColumn(i,"LEG_LEFT_2");
            var LEG_LEFT_3  = dsPerioExam3.GetColumn(i,"LEG_LEFT_3");
            
            var LEG_RIGHT_1     = dsPerioExam3.GetColumn(i,"LEG_RIGHT_1");
            var LEG_RIGHT_2     = dsPerioExam3.GetColumn(i,"LEG_RIGHT_2");
            var LEG_RIGHT_3     = dsPerioExam3.GetColumn(i,"LEG_RIGHT_3");
            
            var LEG_LEFT2_1     = dsPerioExam3.GetColumn(i,"LEG_LEFT2_1");
            var LEG_LEFT2_2     = dsPerioExam3.GetColumn(i,"LEG_LEFT2_2");
            var LEG_LEFT2_3     = dsPerioExam3.GetColumn(i,"LEG_LEFT2_3");
            
            var LEG_RIGHT2_1   = dsPerioExam3.GetColumn(i,"LEG_RIGHT2_1");
            var LEG_RIGHT2_2   = dsPerioExam3.GetColumn(i,"LEG_RIGHT2_2");
            var LEG_RIGHT2_3   = dsPerioExam3.GetColumn(i,"LEG_RIGHT2_3");
            
            var BELLY_1  = dsPerioExam3.GetColumn(i,"BELLY_1");
            var BELLY_2  = dsPerioExam3.GetColumn(i,"BELLY_2");
            var BELLY_3  = dsPerioExam3.GetColumn(i,"BELLY_3");
            
            var MAX_PWR_1  = dsPerioExam3.GetColumn(i,"MAX_PWR_1");
            var MAX_PWR_2  = dsPerioExam3.GetColumn(i,"MAX_PWR_2");
            var MAX_PWR_3  = dsPerioExam3.GetColumn(i,"MAX_PWR_3");
            
            var AVG_PWR_1  = dsPerioExam3.GetColumn(i,"AVG_PWR_1");
            var AVG_PWR_2  = dsPerioExam3.GetColumn(i,"AVG_PWR_2");
            var AVG_PWR_3  = dsPerioExam3.GetColumn(i,"AVG_PWR_3");
            
            var TIRE_IDX_1  = dsPerioExam3.GetColumn(i,"TIRE_IDX_1");
            var TIRE_IDX_2  = dsPerioExam3.GetColumn(i,"TIRE_IDX_2");
            var TIRE_IDX_3  = dsPerioExam3.GetColumn(i,"TIRE_IDX_3");          
            
            // 악력 좌 평균
            if((GRIP_LEFT_1 != "00.00")) {
                if((GRIP_LEFT_1 != "0000")) { cnt ++; }
            }
            if(GRIP_LEFT_2 != "00.00") { 
                if((GRIP_LEFT_2 != "0000")) { cnt ++; }    
            }
            if(GRIP_LEFT_3 != "00.00") { 
                if((GRIP_LEFT_3 != "0000")) { cnt ++; }    
            }
            avg = (
                     (toNumber(SubStr(GRIP_LEFT_1,0,2))+toNumber(SubStr(GRIP_LEFT_2,0,2))+toNumber(SubStr(GRIP_LEFT_3,0,2)))
                    +(toNumber("0."+SubStr(GRIP_LEFT_1,2,2))+toNumber("0."+SubStr(GRIP_LEFT_2,2,2))+toNumber("0."+SubStr(GRIP_LEFT_3,2,2)))
                  ) / cnt;
            
            dsPerioExam3.SetColumn(i,"GRIP_LEFT_AVG", round(avg,2));
            
            // 악력 우 평균
            cnt = 0;
            avg = 0.0;
            if(GRIP_RIGHT_1 != "00.00") { 
                if(GRIP_RIGHT_1 != "0000") { cnt ++; } 
            }
            if(GRIP_RIGHT_2 != "00.00") { 
                if(GRIP_RIGHT_2 != "0000") { cnt ++; } 
            }
            if(GRIP_RIGHT_3 != "00.00") { 
                if(GRIP_RIGHT_3 != "0000") { cnt ++;  }
            }
            avg = (
                     (toNumber(SubStr(GRIP_RIGHT_1,0,2))+toNumber(SubStr(GRIP_RIGHT_2,0,2))+toNumber(SubStr(GRIP_RIGHT_3,0,2)))
                    +(toNumber("0."+SubStr(GRIP_RIGHT_1,2,2))+toNumber("0."+SubStr(GRIP_RIGHT_2,2,2))+toNumber("0."+SubStr(GRIP_RIGHT_3,2,2)))
                  ) / cnt;
            
            dsPerioExam3.SetColumn(i,"GRIP_RIGHT_AVG", round(avg,2));
            
            // 각력 굴근좌 평균
            cnt = 0;
            avg = 0.0;
            if(LEG_LEFT_1 != "00.00") { 
                if(LEG_LEFT_1 != "0000") { cnt ++; } 
            }
            if(LEG_LEFT_2 != "00.00") { 
