// TAB 변경에 따른 조회조건 처리
function tabMain_OnChanged(obj,nOldIndex,nNewindex)
{
	//공통코드 조회
	fcd_GetComboDs(dsInComCd);
	
	if(nNewindex == 0) { 
        cbPerioNo.Enable      = true;
        cbRound.Enable        = true;
        //calDate.Enable        = true;
        cbBeforeRound.Enable  = false;
        cbCurrentRound.Enable = false;
    }
	else if(nNewindex == 1){
        cbPerioNo.Enable      = true;
        cbRound.Enable        = false;
        //calDate.Enable        = false;
        cbBeforeRound.Enable  = true;
        cbCurrentRound.Enable = true;
    }
	else if(nNewindex == 2) {
        cbPerioNo.Enable      = true;
        cbRound.Enable        = false;
        //calDate.Enable        = false;
        cbBeforeRound.Enable  = false;
        cbCurrentRound.Enable = false;
    }
}

// 신체조성측정 로딩후 일자 처리
function dsPerioExam1_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	 if(dsPerioExam1.rowcount > 0) {
         for(var i=0; i<dsPerioExam1.rowcount; i++) {
             if(dsPerioExam1.GetColumn(i, "PT_SCR") == "1") {
                 calFstDt.Value = dsPerioExam1.GetColumn(i, "DT");
                 calSndDt.Value = null;
                 calTrdDt.Value = null;
             }
             else if(dsPerioExam1.GetColumn(i, "PT_SCR") == "2") {
                 calSndDt.Value = dsPerioExam1.GetColumn(i, "DT");
                 calFstDt.Value = null;
                 calTrdDt.Value = null;
             }
             else if(dsPerioExam1.GetColumn(i, "PT_SCR") == "3") {
                 calTrdDt.Value = dsPerioExam1.GetColumn(i, "DT");
                 calFstDt.Value = null;
                 calSndDt.Value = null;
             }
         }
     }
}

// 회차별 비교 로딩후 일자 처리
function dsPerioExam2_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
    var bofDate  = dsPerioExam2.GetColumn(0, "BOF_DT");         // 전회차 일자
    var bofRound = dsPerioExam2.GetColumn(0, "BOF_PT_SCR");     // 전회차
    var curDate  = dsPerioExam2.GetColumn(0, "CUR_DT");         // 현재회차 일자
    var curRound = dsPerioExam2.GetColumn(0, "CUR_PT_SCR");     // 현재회차 
    
    if(dsPerioExam2.rowcount >0) {
        for(var i=0; i<dsPerioExam2.rowcount; i++) {
            if(bofDate != null) {
                if(bofRound == "1") {
                   calFstDt.Value = dsPerioExam2.GetColumn(0, "BOF_DT");
                }
                else if(bofRound == "2") {
                   calSndDt.Value = dsPerioExam2.GetColumn(0, "BOF_DT"); 
                }
                else if(bofRound == "3") {
                   calTrdDt.Value = dsPerioExam2.GetColumn(0, "BOF_DT");
                }
            }
            
            if(curDate != null) {
                if(curRound == "1") {
                   calFstDt.Value = dsPerioExam2.GetColumn(0, "CUR_DT");
                }
                else if(curRound == "2") {
                   calSndDt.Value = dsPerioExam2.GetColumn(0, "CUR_DT"); 
                }
                else if(curRound == "3") {
                   calTrdDt.Value = dsPerioExam2.GetColumn(0, "CUR_DT");
                }
            }
        }
    }    
}

// 종합비교 로딩후 일자 처리
function dsPerioExam3_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if(dsPerioExam3.rowcount > 0) {
        var firstDate   = dsPerioExam3.GetColumn(0, "FST_DT");         // 1차 일자
        var secondDate  = dsPerioExam3.GetColumn(0, "SND_DT");         // 2차 일자
        var thirdDate   = dsPerioExam3.GetColumn(0, "TRD_DT");         // 3차 일자
            
               
        if(firstDate != null) {
            calFstDt.Value = firstDate;
        }
        
        if(secondDate != null) {
            calSndDt.Value = secondDate;
        }
        
        if(thirdDate != null) {
            calTrdDt.Value = thirdDate;
        }
    }
}


// 엑셀 등록 처리
function cmdExcelUpload_OnClick(obj)
{
	if(tabMain.TabIndex == 0) {
        var strFileName;
        var nRow;
        var TR_AMT_IN;
        var TR_AMT_OUT;
        
        FileDialog.Type = "OPEN";
        FileDialog.Filter = "Excel(.xls)|*.xls|";
        
        if (!FileDialog.open()){
            return;
        }
        dsOutSheet.ClearData();
        dsPerioExam1.ClearData();
        strFileName = FileDialog.FilePath + "\\" + FileDialog.FileName;
        ext_ExcelImportByIndex(strFileName, 0, dsOutSheet.ID);
        nRow = dsOutSheet.RowCount();
   
        for (var i=2; i < nRow; i++)
        {       
            dsPerioExam1.AddRow();
            dsOutSheet.CopyToRow(i, dsPerioExam1, i - 2, "CAND_NO=Col02,NM_KOR=Col03,GRIP_LEFT_REC=Col04,GRIP_RIGHT_REC=Col05,LEG_LEFT_REC=Col06,LEG_RIGHT_REC=Col07,LEG_LEFT_REC2=Col08,LEG_RIGHT_REC2=Col09,BELLY_REC=Col10,MAX_PWR_REC=Col11,AVG_PWR_REC=Col12,TIRE_IDX_REC=Col13");
        }
	}
}
function fcPrint(){

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "";          //레포트 파일명
	var sDataSet  = "";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "";
	var sRptDrct  = 0;   
	if (tabMain.TabIndex == 0){ 
		 sRptNm    = "d02000019_1.jrf";          //레포트 파일명
		 sDataSet  = " dsPerioExam1=dsPerioExam1 ";    //"UBI 쿼리명, MIP Dataset명"
		 sArg      = "Round="+cbRound.Value;
		 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
		 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	
	}else if (tabMain.TabIndex == 1){ 
		 sRptNm    = "d02000019_2.jrf";          //레포트 파일명
		 sDataSet  = " dsPerioExam2=dsPerioExam2 ";    //"UBI 쿼리명, MIP Dataset명"
		 sArg      = "Round1="+cbBeforeRound.Value;
		 sArg     += " Round2="+cbCurrentRound.Value;         
		 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
		 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	
	}else if (tabMain.TabIndex == 2){ 
		 sRptNm    = "d02000019_3.jrf";          //레포트 파일명
		 sDataSet  = " dsPerioExam3=dsPerioExam3 ";    //"UBI 쿼리명, MIP Dataset명"
		 sArg      = "";       
		 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
		 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	
	}
}
