/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj)
{
	
	sBUTTONLIST   = 'T'+sInpt+'FTFT';
    
 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);
	
	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);
	
	fcd_GetComboDs(dsInComCd);
    // 화면 초기화
	
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/

function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsMatCodeListValue";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  clear 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {
        edMat.Enable = true;
	    edMat.text = "" ;
        dsRaceListValue.ClearData();
    }    
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch() {

    edMat.text = Trim(cbMat.text);		
    edMat.Enable = false;  // 조회시 대진코드 수정못하게
	
	var sServiceName = "d02000020-service:searchRaceList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsRaceListValue=dsRaceListValue "; 
    
    fcd_AddParam("MAT_CD", Trim(cbMat.text));
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false); 
}

function fcMatCode() {
	
	var sServiceName = "d02000020-service:searchMatCd";
    var sInDataSet   = "";
    var sOutDataSet  = " dsMatCodeListValue=dsMatCodeListValue "; 
    
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
      
}
/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  
	//변경된 자료가 있는지 체크
	if (edMat.text == ""){
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}
	if ( !fnc_DatasetChangeCheck(dsRaceListValue)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	return true;
}
function fcSave(obj) {
    
    if ( !fcSaveCheck() ) return;
    //trace(dsRaceListValue.SaveXML());
	//fnc_DataSetPrint(dsRaceListValue);	
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
    
    sServiceName = "d02000020-service:saveEnforRace";
    sInDataSet   = " dsRaceListValue=dsRaceListValue:u "; 
    sOutDataSet  = " dsRaceListValue=dsRaceListValue ";
    
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack" );
    fcMatCode();
}

