}
/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	
	//DataSet의 Header 정보가 있을 때  Add 처리
	if ( obj.GetForm().id == "divBtnRow" ) {
       if(!fnc_IsNull(edMat.Text)) {         
			dsRaceListValue.addrow();         
			dsRaceListValue.setColumn(dsRaceListValue.rowposition, "MAT_CD", edMat.text);
		}else{
			fnc_Message(SNIS_COM_1035, "N","대진표코드");	
		}
       //dsRaceListValue.SetColumn(dsRaceListValue.lastrow, "GRIDSTAT", "1"); 
   }        
}
/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( obj.GetForm().id == "divBtnRow" ) {
            var sArg = "";

			if ( !fnc_DeleteCheck(dsRaceListValue, "chk", "1") ) return;

	//		dsOutEduAssign_OnRowPosChanged();  	
	} 
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
    //오류가 없이 정상 처리되었다면...
        
        // 공통코드 조회 후
		if ( sServiceName == "common-service:searchCode" ) {
			fcMatCode();
			fcInit();
			return;
		}
        
        
        if(sServiceName =="d02000020-service:searchRaceList"){
			return;
		}
		
		if(sServiceName =="d02000020-service:saveEnforRace"){
		
			edMat.Enable = true;
			edMat.Text = "";
            fcMatCode();
            return;
		}
	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

