>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

function cbMat_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();
}
