+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj)
{
	
	sBUTTONLIST   = 'T'+sInpt+'FTFT';
    
 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);
	
	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);
			
	fcd_GetComboDs(dsInComCd);
	//dsRaceListValue.addRow(); // 화면 출력시 수정가능하도록
}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/

function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsAssignListValue";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}
function fcMatCode() {
	
	var sServiceName = "d02000021-service:searchMatCd";
    var sInDataSet   = "";
    var sOutDataSet  = " dsMatCodeListValue=dsMatCodeListValue "; 
    
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
      
}
function fccbinit(){
	cbStltRace.Enable = false;
	cbAdvRace.Enable = false;
}
/*-------------------------------------------------------------------------------------------------+
>>  clear 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj)
{
	cbMat.text = "";
	fcSearch();
	cbStltRace.Enable = true;
	cbAdvRace.Enable = true;
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  

	if ( !fnc_DatasetChangeCheck(dsAssignListValue)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	return true;
}
function fcSave(obj) {

    if ( !fcSaveCheck() ) return;
    
	fnc_DataSetPrint(dsAssignListValue);
	
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
    
    sServiceName = "d02000021-service:saveGrdAssign";
    sInDataSet   = " dsAssignListValue=dsAssignListValue:u ";      
    sOutDataSet  = " dsAssignListValue=dsAssignListValue ";
    
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
   	    
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch() {
	
	edMat.text = Trim(cbMat.text);
	var racerPerioNo = null;
	var sServiceName = "d02000021-service:searchAssign";
    var sInDataSet   = "";
    var sOutDataSet  = "dsAssignListValue=dsAssignListValue "; 
    
    fcd_AddParam("MAT_CD", Trim(cbMat.value));
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false); 
}
/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	
	//DataSet의 Header 정보가 있을 때  Add 처리
	if ( obj.GetForm().id == "divBtnRow" ) {
            
       dsAssignListValue.addrow();
       cbStltRace.Enable = true;
	   cbAdvRace.Enable = true;
       dsAssignListValue.setColumn(dsAssignListValue.Currow, "MAT_CD", edMat.value);
       //dsAssignListValue.SetColumn(dsAssignListValue.lastrow, "GRIDSTAT", "1"); 
   }        
}
/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	
	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( obj.GetForm().id == "divBtnRow" ) {
            var sArg = "";

			if ( !fnc_DeleteCheck(dsAssignListValue, "chk", "1") ) return;

	//		dsOutEduAssign_OnRowPosChanged();  	
	} 
}

