/*-------------------------------------------------------------------------------------------------+
>>  메뉴상세 조회 선작업
+-------------------------------------------------------------------------------------------------*/
function fcGetMenuDetailBefore(obj) {
    var clickedRow = obj.GetCurrow();
    fcGetMenuDetail(clickedRow);   
}
/*-------------------------------------------------------------------------------------------------+
>>  메뉴상세 조회
+-------------------------------------------------------------------------------------------------*/
function fcGetMenuDetail(sIndex) {

    if(length(dsAssignListValue.getColumn(sIndex, "MAT_CD")) > 0 ) {
        cbStltRace.Value    = 	dsAssignListValue.getColumn(sIndex, "STLT_RACE_GRD_CD"	);
		cbAdvRace.Value     =	dsAssignListValue.getColumn(sIndex, "ADV_RACE_GRD_CD"	);
		cbStrRank.Value	    =	dsAssignListValue.getColumn(sIndex, "STR_RANK"	);
		cbEndRank.Value	    =   dsAssignListValue.getColumn(sIndex, "END_RANK"	);
		cbRaceNo.Value	    =	dsAssignListValue.getColumn(sIndex, "RACE_NO"	);
		cbEquRank.Value		= 	dsAssignListValue.getColumn(sIndex, "EQU_RANK"	);
		cbEquRankNo.Value	= 	dsAssignListValue.getColumn(sIndex, "EQU_RANK_NO"	);
		cbEquRank2.Value	= 	dsAssignListValue.getColumn(sIndex, "EQU_RANK2"	);
		cbEquRankNo2.Value	= 	dsAssignListValue.getColumn(sIndex, "EQU_RANK_NO2"	);
		
    }
}
