/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
            // 공통코드 조회 후
	if ( sServiceName == "common-service:searchCode" ) {
		fcMatCode();
		fccbinit();
		return;
	}
	
	if (sServiceName == "d02000021-service:searchAssign"){
	//sMenuListNum = dsAssignListValue.rowCount;
		dsAssignListValue.AddColumn("CHK");
		fcGetMenuDetail("0");
		return;
    }
    
    if (sServiceName == "d02000021-service:saveGrdAssign"){
		fcMatCode();
		fccbinit();
		return;
    }
	fcPostProcess(sServiceName);
	
}

