function dsAssignListValue_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	fcGetMenuDetail(nRow);
}


function grdAssign_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	edStlt.Text = dsAssignListValue.RowCount();
	edAdv = dsAssignListValue.row;
	if((dsAssignListValue.RowCount() - dsAssignListValue.row) > 1){
		fccbinit();
	}
}

