//조회 
function fcSearch(obj) {
	fcSearchCheck();
    
    var sServiceName = "d02000022-service:searchCStartTimeLength";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutCStartTimeLength=dsOutCStartTimeLength";
    
    fcd_AddParam("STND_YEAR", cboStndYear.Value);
    fcd_AddParam("STND_MONTH", cboStndMonth.Value);
    fcd_AddParam("AM_PM_GBN", cboAmPmGbn.Value);
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutCStartTimeLength))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	
	// 필수항목체크
	oDataSet          = dsOutCStartTimeLength;
	sMandatoryColID   = "RACE_YY|RACE_MM|AM_PM_GBN|RACE_NO";
	sMandatoryColName = "년도|월|오전/오후|경주번호";
	oGrid             = grdPageList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
	var sServiceName = "d02000022-service:saveCStartTimeLength";
    var sInDataSet   = "dsOutCStartTimeLength=dsOutCStartTimeLength:u";
    var sOutDataSet  = "dsOutCStartTimeLength=dsOutCStartTimeLength";
	
	fcd_AddParam("STND_YEAR", cboStndYear.Value);
    fcd_AddParam("STND_MONTH", cboStndMonth.Value);
    fcd_AddParam("AM_PM_GBN", cboAmPmGbn.Value);
    
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

