/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
	// 공통코드 조회 후
	if ( sServiceName == "common-service:searchCode" ) {
		fcSearch();
	}
	// 조회 후
	if ( sServiceName == "d02000022-service:searchCStartTimeLength" ) {
	    if(dsOutCStartTimeLength.GetRowCount() > 0){
			fnc_SetStatus(SNIS_CAN_0003, dsOutCStartTimeLength.GetRowCount());
	    }
	}
	// 저장 후
	if ( sServiceName == "d02000022-service:saveCStartTimeLength" ) {
		if(dsOutCStartTimeLength.GetRowCount() > 0){
			fnc_SetStatus(SNIS_CAN_0003, dsOutCStartTimeLength.GetRowCount());
	    }
	}
	
	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {
	
}

