//row 변경시 
function dsOutCStartTimeLength_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
	trace(dsOutCStartTimeLength.GetRowType(nRow));
	if (dsOutCStartTimeLength.GetRowType(nRow) == "insert")
	{
		cboiStndYear.Enable		= true;
		cboiStndMonth.Enable 	= true;
		cboiAmPmGbn.Enable 		= true;
		cboRaceNo.Enable 		= true;
	}
	else
	{
		cboiStndYear.Enable 	= false;
		cboiStndMonth.Enable 	= false;
		cboiAmPmGbn.Enable 		= false;
		cboRaceNo.Enable 		= false;
	}
}


//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutCStartTimeLength_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	if(LATEST_ROW_POS > 0) dsOutCStartTimeLength.Row = LATEST_ROW_POS;
	dsOutCStartTimeLength_OnRowPosChanged(obj, LATEST_ROW_POS, LATEST_ROW_POS);
}


function dsOutCStartTimeLength_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "RACELEN") return;
	// 출발시간
	if (strColumnID == "STR_TIME")
	{
		var sSTR_TIME = dsOutCStartTimeLength.GetColumn(nRow, "STR_TIME");
		if (length(sSTR_TIME) != 4 || IsDigit(sSTR_TIME) == false || ToNumber(Left(sSTR_TIME, 2)) > 24 || ToNumber(Left(sSTR_TIME, 2)) < 0 || ToNumber(Right(sSTR_TIME, 2)) < 0 || ToNumber(Right(sSTR_TIME, 2)) > 59)
		{
			dsOutCStartTimeLength.SetColumn(nRow, "STR_TIME", varOldValue);
			fnc_Message(SNIS_CAN_M001, "N");
			return false;
		}
	}
	// 주회수
	if (strColumnID == "CCIT_CNT")
	{
		var sCCIT_CNT = dsOutCStartTimeLength.GetColumn(nRow, "CCIT_CNT");
		var nFindRow  = dsOutCcitCnt.FindRow("CD", sCCIT_CNT);
		dsOutCStartTimeLength.SetColumn(nRow, "RACELEN", dsOutCcitCnt.GetColumn(nFindRow, "RACELEN"));
	}
}
