function fcSaveCheck(obj) {  

	return true;
}
function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
   
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
    
    sServiceName = "d02000023-service:saveDetlRace";
    sInDataSet   = "dsDetlRaceScheValue=dsDetlRaceScheValue:u dsMatGrd=dsMatGrd:u "; 
    sOutDataSet  = "dsDetlRaceScheValue=dsDetlRaceScheValue dsMatGrd=dsMatGrd ";
    
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	if(fnc_IsNull(cboYear.value)) {    
        //조회할 자료가 없음
		fnc_Message(SNIS_CAN_0008, "N","기수,회차");
		return false;
	}
	return true;
}
function fcSearch(obj) {

    if ( !fcSearchCheck() ) return;
	
	var sServiceName = "d02000023-service:searchDetailRace";
    var sInDataSet   = "";
    var sOutDataSet  = "dsDetlRaceScheValue=dsDetlRaceScheValue";   
		sOutDataSet += " dsMatGrd=dsMatGrd";
	
	
    fcd_AddParam("Raceyy",cboYear.value);
    
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false); 
    
}
function fcSubSearch(nRow){
	
	var sServiceName = "d02000023-service:searchDetailMatGrd";
    var sInDataSet   = "";
    var sOutDataSet  = " dsMatGrd=dsMatGrd";  

	fcd_AddParam(trim("Raceyy"),dsDetlRaceScheValue.getColumn(nRow,"RACE_YY"));
	fcd_AddParam(trim("RaceRndNo"),dsDetlRaceScheValue.getColumn(nRow,"ROUND"));
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false); 
}
*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리
	if ( obj.GetForm().id == "divBtnRow" ) {
       // dsDetlRaceScheValue.ClearData();    
        dsDetlRaceScheValue.addrow();
        dsMatGrd.ClearData();
        dsDetlRaceScheValue.SetColumn(dsDetlRaceScheValue.lastrow, "GRIDSTAT", "1"); 
    }
    if ( obj.GetForm().id == "divBtnRow1") {
		/*
		if((cbRaceYY.Text =="")||(cbRound.Text = "")){
			fnc_Message(SNIS_CAN_D002, "N","기수,회차");
			return false;
		}*/

		dsMatGrd.addrow();
		dsMatGrd.SetColumn(dsMatGrd.currow, "RACE_YY",trim(cbRaceYY1.Text));
		dsMatGrd.SetColumn(dsMatGrd.currow, "ROUND",trim(edRound.Text));
		dsMatGrd.SetColumn(dsMatGrd.lastrow, "GRIDSTAT", "1"); 

    }
    
}
/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	
	//DataSet의 Header 정보가 있을 때  Add 처리

	if ( obj.GetForm().id == "divBtnRow" ) {
            var sArg = "";
			if ( !fnc_DeleteCheck(dsDetlRaceScheValue, "chk", "1") ) return;

	//		dsOutEduAssign_OnRowPosChanged();  	
	} 
		if ( obj.GetForm().id == "divBtnRow1" ) {
            var sArg = "";
			if ( !fnc_DeleteCheck(dsMatGrd, "chk", "1") ) return;
	} 
}
/*-------------------------------------------------------------------------------------------------+
>>  clear 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {
    
        dsDetlRaceScheValue.ClearData();
        dsMatGrd.ClearData();
        fcd_GetComboDs(dsInComCd);
    }    
}
/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}

