function fcToExcel(obj) {
    var sExcelList = "grdDetailRndRace:세부시행일정|grdMatGrd:등급";
    fcd_OpenExcelListExport(sExcelList);
}


/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
