/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
	if ( sServiceName = "d02000023-service:searchRaceYY" ){
		
		fcMatCode();		//대진표 combo
		fcSearch();
	}
	if ( sServiceName == "common-service:searchCode" ) {
		
		fcYear();					                  // 년도 조회 
		//fcd_GetRound(cboYear.value); 
	}
	
    if (sServiceName == "d02000023-service:searchDetailRace"){
  
		dsDetlRaceScheValue.AddColumn("CHK");
		
		fcSubSearch(0);
		
		fcSeltround();  //선발회차 활성
		//alert(dsDetlRaceScheValue.getColumn(2,"AM_PM_GBN"));
    }
    if (sServiceName == "d02000023-service:saveDetlRace"){ 
    
    }
    
    if (sServiceName == "d02000023-service:searchDetailMatGrd"){
		
		fcSeltround();  //선발회차 활성
    }
    
    
	fcPostProcess(sServiceName);
	
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

} 
function fcGrdOnExpandEdit(objGrd, nRow, nCell, strVal, nPivotIndex){
	fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
}
function grdDetailRndRace_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	fcSubSearch(nRow);
}


function cbRaceMeth_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
		if (cbRaceMeth.Value != "002"){
			edSha.Enable = false;
		}else{
			edSha.Enable = true;
		}

}
function fcSeltround(){
		// 수정토너먼트 일 경우 선발회차 활성화
		if (dsDetlRaceScheValue.getColumn(dsDetlRaceScheValue.currow,"RACE_METH") !="002"){
			edSha.Enable = false;
		}else{
			edSha.Enable = true;
		}
}
function cboYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcd_GetRound(strText); 
	cboRound.Index = 0 ; 
	fcSearch();
	fcSubSearch(0);
}
function grdDetailRndRace_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}
