function fcMatCode()
{
	var sServiceName = "d02000024-service:searchMatCd";
    var sInDataSet   = "";
    var sOutDataSet  = " dsMatCode=dsMatCode "; 
    
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}
/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
function fcSearch(){
	//후보생배정 TABLE조회
	dsCandAssign.Clear();
	
	var sServiceName = "d02000024-service:searchCandAssign";
    var sInDataSet   = "";
    var sOutDataSet  = "dsCandAssign=dsCandAssign";
    
    fcd_AddParam(Trim("RACE_YY"), 	cboYear.Value	);
	fcd_AddParam(Trim("ROUND"), 	cboRound.Value     	);
	if (rd1.Value == 0){ //선수등급선택
		fcd_AddParam(Trim("GRD"), 	 cbGrd.Value     	);
	}else{
		fcd_AddParam(Trim("MAT_CD"), 	 cbMat.Value     	);
	}
	fcd_AddParam(Trim("RADIO1"), 	rd1.Value     	);
	fcd_AddParam(Trim("RADIO2"), 	rdOrder.Value     	);
	fcd_AddParam(Trim("SEARCH_CHK"), "2" );  //  선수명단에서 조회
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}
/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	
	if ( dsCfmFg.getcolumn(0,"CFM_FG") == "Y"  ){
		fnc_Message(SNIS_CAN_D013, "N", "후보생배정");
        return false;
    }
    return true;
}
function fcSave(obj){

	if ( !fcSaveCheck(obj) ) return;
	
	var sServiceName = "d02000024-service:saveCandAssign";
    var sInDataSet   = " dsCandAssign=dsCandAssign ";      
    var sOutDataSet  = " dsCandAssign=dsCandAssign ";
    fnc_DataSetPrint(dsCandAssign);
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	
}

