    fcd_AddParam(Trim("RACE_YY"), 	cboYear.Value	);
	fcd_AddParam(Trim("ROUND"), 	cboRound.Value     	);
	if (rd1.Value == 0){ //선수등급선택
		fcd_AddParam(Trim("GRD"), 	 cbGrd.Value     	);
	}else{
		fcd_AddParam(Trim("MAT_CD"), 	 cbMat.Value     	);
	}
	fcd_AddParam(Trim("RADIO1"), 	rd1.Value     	);
	fcd_AddParam(Trim("RADIO2"), 	rdOrder.Value     	);
