/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
    
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
    
    if ( sServiceName == "common-service:searchCode" ) {
    	fcd_GetYear();					                  // 년도 조회 
		fcd_GetRound(cboYear.value); 
    }
    
    if ( sServiceName == "common-service:searchRound" ) {
		
		cboRound_OnChanged();
		
    }
    if (sServiceName == "d02000024-service:searchDetailRace"){
		
		fcSearchCfmFg();
		fcSearch();
    }
    
    if (sServiceName == "d02000024-service:saveCandAssign" ) {

    }
    
    if (sServiceName == "d02000024-service:searchCandAssign" ) {

		 var iPlayer = 0 , ileader = 0 , icand = 0 ;
		 
		 for(var i=0; i<dsCandAssign.rowcount; i++) {
			if (dsCandAssign.GetColumn(i,"DCSN_GBN") == "0"){          
				  switch (dsCandAssign.GetColumn(i,"GBN"))
				  {
					  case 1 :	iPlayer = iPlayer + 1;	break;
					  case 2 :  ileader = ileader + 1;	break;
					  case 3 :	icand   = icand   + 1;	break;
				  }//switch
              }//if
		}	//for	
		edPlayer.Text = iPlayer;  //출전
		edLeader.Text = ileader;  //유도
		edCand.Text = icand;  	  //후보
		
    }
	fcPostProcess(sServiceName);
	
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

} 
function cboRound_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	var sServiceName = "d02000024-service:searchDetailRace";
    var sInDataSet   = "";
    var sOutDataSet  = " dsDetlRaceScheValue=dsDetlRaceScheValue dsMatGrd=dsMatGrd ";  
	
	fcd_AddParam(trim("Raceyy"),cboYear.Value);
	fcd_AddParam(trim("RaceRndNo"),cboRound.Value);
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false); 
}


function rd1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	
}

function rd1_OnClick(obj)
{
   if(rd1.Value == "1"){
   		cbGrd.Visible = false;
		cbMat.Visible = true;
   }else{
		cbGrd.Visible = true;
		cbMat.Visible = false;
   
   }
}


function cboYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcd_GetRound(strText); 
	cboRound.Index = 0 ; 
	fcSearch();
}


function dsCandAssign_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	
		 var iPlayer = 0 , ileader = 0 , icand = 0 ;
		 
		 for(var i=0; i<dsCandAssign.rowcount; i++) {        
			if (dsCandAssign.GetColumn(i,"DCSN_GBN") == "0"){    
              switch (dsCandAssign.GetColumn(i,"GBN"))
              {
				  case 1 :	iPlayer = iPlayer + 1;	break;
				  case 2 :  ileader = ileader + 1;	break;
				  case 3 :	icand   = icand   + 1;	break;
              } //switch
            }//if
		}//for		
		edPlayer.Text = iPlayer;  //출전
		edLeader.Text = ileader;  //유도
		edCand.Text = icand;  	  //후보
}


function grdCandAssign_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	fnc_GridSort(obj, object(obj.BindDataset), nCell);
}
