/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj)
{
	//사용자 권한 설정
    sBUTTONLIST   = 'F'+sInpt+'TFFT';
 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);
	
	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);
	
	    
    fcd_GetYear();					                  // 년도 조회 
    fcd_GetRound(cboYear.value);                      // 회차 조회
    
    cboYear.SetFocus();  
}

// 회차 조회 
function cboYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcd_GetRound(strText); 
	cboRound.Index = 0 ; 
	fcSearch(obj);
}


//경주번호 조회 
function cboRound_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch(obj);
}

