+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";//"dsTbdbGroupOrg1|dsTbdbGroupOrg2|dsTbdbGroupOrg3|dsTbdbGroupOrg4|dsTbdbGroupOrg5|dsTbdbGroupOrg6|dsTbdbGroupOrg7|dsTbdbGroupOrg8|dsTbdbGroupOrg9|dsTbdbGroupOrg10|dsTbdbGroupOrg11|dsTbdbGroupOrg12";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}
/*-------------------------------------------------------------------------------------------------+
>>  화면 삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelete(obj){
	if ( fnc_Message(SNIS_CAN_D009, "Y", "경주번호확정") ) {
		fcDeleteRaceRec(); 
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDeleteRaceRec(){

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";
    
    if ( !fcDeleteCheck(obj) ) return;
	
	sServiceName = "d02000027-service:deleteTbdbGroupOrg";	
	sInDataSet  = "dsTbdbGroupOrg1=dsTbdbGroupOrg1:i" ; 
	sOutDataSet  = "" ; 
   
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	
	
}
