>>  삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDeleteCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( cboYear.value == "" || cboRound.value == "" )
	{
		fnc_Message(SNIS_COM_1036, "N");  // 삭제할 자료를 조회해 주십시오. 
		return false;
	}

    return true;
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcEnd(obj) {
	//화면 종료
	Close();	
}


