/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

//  마지막 번호에 선수가 없을 경우 Row 삭제(5차까지만만 우선)   
	  var cnt1 = dsTbdbGroupOrg1.RowCount();
	  var cnt2 = dsTbdbGroupOrg2.RowCount();
	  var cnt3 = dsTbdbGroupOrg3.RowCount();
	  var cnt4 = dsTbdbGroupOrg4.RowCount();
	  var cnt5 = dsTbdbGroupOrg5.RowCount();

      var j1 = 0;
      var j2 = 0;
      var j3 = 0;
      var j4 = 0;
      var j5 = 0;
     
	  for (i = 0;i<cnt1;i++){
			if(dsTbdbGroupOrg1.GetColumn(i,"CAND_NM") ==null){
				dsTbdbGroupOrg1.DeleteRow(j1);
			}else{
				j1++;
			}
		}
	  for (i = 0;i<cnt2;i++){
			if(dsTbdbGroupOrg2.GetColumn(i,"CAND_NM") ==null){
				dsTbdbGroupOrg2.DeleteRow(j2);
			}else{
				j2++;
			}
		}
	  for (i = 0;i<cnt3;i++){
			if(dsTbdbGroupOrg3.GetColumn(i,"CAND_NM") ==null){
				dsTbdbGroupOrg3.DeleteRow(j3);
			}else{
				j3++;
			}
		}
	  for (i = 0;i<cnt4;i++){
			if(dsTbdbGroupOrg4.GetColumn(i,"CAND_NM") ==null){
				dsTbdbGroupOrg4.DeleteRow(j4);
			}else{
				j4++;
			}
		}
	  for (i = 0;i<cnt5;i++){
			if(dsTbdbGroupOrg5.GetColumn(i,"CAND_NM") ==null){
				dsTbdbGroupOrg5.DeleteRow(j5);
			}else{
				j5++;
			}
		}
			
	for (var i=1; i<= 12; i++) { 
		oDataSet          = object("dsTbdbGroupOrg"+i);
		sMandatoryColID   = "RACE_YY|ROUND|RACE_NO|CAND_NO|BACK_NO";
		sMandatoryColName = "기준년도|회차|경주번호|후보생번호|배번";
		oGrid             = "";
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}
		
    } 
     
	if ( dsCfmFg.getcolumn(0,"CFM_FG") == "Y"  ){
		fnc_Message(SNIS_CAN_D013, "N", "경주기록확정");
        return false;
    }
    return true;
}



function fcSave(obj) {

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";
    
    if ( !fcSaveCheck(obj) ) return;

	// master / detail 삭제 후 저장시킴 
	sServiceName = "d02000027-service:saveTbdbGroupOrg1";	
	sInDataSet  = "dsTbdbGroupOrg1=dsTbdbGroupOrg1:i"
	             + " dsTbdbGroupOrg2=dsTbdbGroupOrg2:i" 
	             + " dsTbdbGroupOrg3=dsTbdbGroupOrg3:i" 
	             + " dsTbdbGroupOrg4=dsTbdbGroupOrg4:i" 
	             + " dsTbdbGroupOrg5=dsTbdbGroupOrg5:i" 
	             + " dsTbdbGroupOrg6=dsTbdbGroupOrg6:i" 
	             + " dsTbdbGroupOrg7=dsTbdbGroupOrg7:i" 
	             + " dsTbdbGroupOrg8=dsTbdbGroupOrg8:i" 
	             + " dsTbdbGroupOrg9=dsTbdbGroupOrg9:i" 
	             + " dsTbdbGroupOrg10=dsTbdbGroupOrg10:i" 
	             + " dsTbdbGroupOrg11=dsTbdbGroupOrg11:i"
	             + " dsTbdbGroupOrg12=dsTbdbGroupOrg12:i" ; 
	sOutDataSet  = "dsTbdbGroupOrg1=dsTbdbGroupOrg1"
	             + " dsTbdbGroupOrg2=dsTbdbGroupOrg2" 
	             + " dsTbdbGroupOrg3=dsTbdbGroupOrg3" 
	             + " dsTbdbGroupOrg4=dsTbdbGroupOrg4" 
	             + " dsTbdbGroupOrg5=dsTbdbGroupOrg5" 
	             + " dsTbdbGroupOrg6=dsTbdbGroupOrg6" 
	             + " dsTbdbGroupOrg7=dsTbdbGroupOrg7" 
	             + " dsTbdbGroupOrg8=dsTbdbGroupOrg8" 
	             + " dsTbdbGroupOrg9=dsTbdbGroupOrg9" 
	             + " dsTbdbGroupOrg10=dsTbdbGroupOrg10" 
	             + " dsTbdbGroupOrg11=dsTbdbGroupOrg11"
	             + " dsTbdbGroupOrg12=dsTbdbGroupOrg12" ; 
   
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	
}

