	object(object(obj.id).BindDataset).SetColumn(nRow,"ELIM3_CNT",sFrom_ELIM3_CNT); 	
	object(object(obj.id).BindDataset).SetColumn(nRow,"TOT_RANK",sFrom_TOT_RANK); 	
	object(object(obj.id).BindDataset).SetColumn(nRow,"ESTM_SCR",sFrom_ESTM_SCR); 	
	object(object(obj.id).BindDataset).SetColumn(nRow,"BF_ROUND",sFrom_BF_ROUND); 	
	object(object(obj.id).BindDataset).SetColumn(nRow,"BF_RACE",sFrom_BF_RACE); 	
	object(object(obj.id).BindDataset).SetColumn(nRow,"RANK_OUT",sFrom_RANK_OUT); 	

    sGrdRow = '' ; 
    
    reCaluclateAvg();
}

