{
	var sumAvg = 0;
	var cnt = 0;
	

	for( var i=0; i < dsTbdbGroupOrg1.RowCount(); i++ )
	{
		if( dsTbdbGroupOrg1.GetColumn(i,"CAND_NM") !=null )
		{
			sumAvg = sumAvg + dsTbdbGroupOrg1.GetColumn(i,"TOT_AVG");
			cnt++;
		}
	}
	
	edAvg1.Text = sumAvg/cnt;
	
	sumAvg = 0;
