function fcSave(obj){
	if ( !fcSaveCheck() ) return;
   
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    
    
    sServiceName = "d02000028-service:saveInduceDcsn";
    sInDataSet   = " dsInduceListValue=dsInduceListValue:u "; 
    sOutDataSet  = " dsInduceListValue=dsInduceListValue ";
    
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(){
	
	var sServiceName = "d02000028-service:searchDetailRace";
    var sInDataSet   = "";
    var sOutDataSet  = " dsDetlRaceScheValue=dsDetlRaceScheValue dsCandListValue=dsCandListValue dsInduceListValue=dsInduceListValue ";  
	
	fcd_AddParam(trim("Raceyy"),cboYear.Value);
	fcd_AddParam(trim("RaceRndNo"),cboRound.Value);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false); 
	
	}
/*-------------------------------------------------------------------------------------------------+
>>  clear 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {
		
		cboRaceNo.Value = "";
        cboRound.Value = "";
        dsInduceListValue.ClearData();
        dsDetlRaceScheValue.ClearData();
        dsCandListValue.ClearData();
        fcd_GetComboDs(dsInComCd);
    }    
}

