/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
    
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
	if ( sServiceName == "common-service:searchCode" ) {
		fcd_GetYear();					                  // 년도 조회 
    }
    if ( sServiceName == "common-service:searchYear" ) {
		
		fcd_GetRound(cboYear.value); 
    }
    if ( sServiceName == "common-service:searchRound" ) {
		fcd_GetRaceNo2(cboYear.value, cboRound.value); 	  //경주번호 조회	
    }
    if ( sServiceName = "common-service:searchRaceNo2" ) {
		fcSearch();
    }
	if(sServiceName = "d02000028-service:searchDetailRace"){
	
		//fnc_DataSetPrint(dsCandListValue);
	}
	
	if(sServiceName = "d02000028-service:saveInduceDcsn"){
		
		//fnc_DataSetPrint(dsCandListValue);
	}
	fcPostProcess(sServiceName);
}

