function cboRound_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcd_GetRaceNo(cboYear.value, strText); 	
	cboRaceNo.Index = 0 ; 
	//fcSearch();
}

function grdCandAssion_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) // 경주별 유도원 선택
{
	var cnt = dsInduceListValue.rowcount();
    var strCandNo = "";
    var option = 0;
    strCandNo = dsCandListValue.GetColumn(nRow,"CAND_NO");
	for(var i = 0; i <= cnt ; i++){ 
		
	//	if (strCandNo == dsInduceListValue.GetColumn(i,"CAND_NO")){ // 동일인 CHECK <- 한선수가 여러번 선두유도 가능하면 주석처리
	//		option = option + 1;                                        이나면 주석풀기   
	//	}
		if (cboRaceNo.Value ==  dsInduceListValue.GetColumn(i,"RACE_NO")){   // 경주번호 중복시
			option = option + 1;
		}
	}

	if  (option == 0){
			dsInduceListValue.AddRow();
			dsInduceListValue.SetColumn(cnt,"RACE_YY",cboYear.Value);
			dsInduceListValue.SetColumn(cnt,"ROUND",cboRound.Value);
			dsInduceListValue.SetColumn(cnt,"RACE_NO",cboRaceNo.Value);
			dsInduceListValue.SetColumn(cnt,"CAND_NO",dsCandListValue.GetColumn(nRow,"CAND_NO"));
			dsInduceListValue.SetColumn(cnt,"CAND_NM",dsCandListValue.GetColumn(nRow,"CAND_NM"));	
		}else{
		fnc_Message(SNIS_CAN_J001, "N");
		}
}

function cboYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcd_GetRound(strText); 
	cboRound.Index = 0 ; 
	//fcSearch();
}
