/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj)
{
	//사용자 권한 설정
    sBUTTONLIST   = 'F'+sInpt+'FFFT'; 
 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);


    fcd_GetYear();					                  // 년도 조회 
    fcd_GetRound(cboYear.Value);                      // 회차 조회

	// 화면 초기화
	//fcInit();

    cboYear.SetFocus();  

}

/*-------------------------------------------------------------------------------------------------+
>>  clear 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsTemp.ClearData();
	dsTempSame.ClearData();
	dsTempSame2.ClearData();
	dsTbdbGroupOrg1.ClearData(); 
	dsTbdbGroupOrg2.ClearData(); 
	dsTbdbGroupOrg3.ClearData(); 
	dsTbdbGroupOrg4.ClearData(); 
	dsTbdbGroupOrg5.ClearData(); 
	dsTbdbGroupOrg6.ClearData(); 
	dsTbdbGroupOrg7.ClearData(); 
	dsTbdbGroupOrg8.ClearData(); 
}


/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UnLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";//dsTbdbRaceRecDetl1|dsTbdbRaceRecDetl2|dsTbdbRaceRecDetl3|dsTbdbRaceRecDetl4|dsTbdbRaceRecDetl5|dsTbdbRaceRecDetl6|dsTbdbRaceRecDetl7|dsTbdbRaceRecDetl8";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
