}


/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) {


            return;
     }

    switch(sServiceName) {


        // 검색조건으로 조회했을 경우
        case "d02000032-service:searchTbdbRaceRecMst001" :		//dsTitleSearch 조회 

			if (dsTitleSearch.GetColumn(0,"RACE_METH") == '003'){
				 fnc_Message(SNIS_CAN_D011, "N", "경주급별배정"); 				
			} 
            break;                     

        case "common-service:searchYear" :    			// 년도 조회 완료시  
            break;   


        case "common-service:searchRound" :    			// 회차 조회 완료시  
			cboMatCdSearch(); //대진표조회 
            break;   

        case "d02000029-service:searchMatCd" : 			//대진표 조회 완료시  
            cboMatCd.index = 0 ; 
            cboAdvRaceGrdCdSearch(); 
            break; 

        case "d02000029-service:searchAdvRaceGrdCd"  : 	// 진출경주 완료시  			
			cboAdvRaceGrdCd.index = 0 ; 
			cboStltRaceGrdCdSearch();     				//  선발선거 combo값 조회 

			break; 

        case "d02000029-service:searchStltRaceGrdCd" : //선발선거 완료시 
            cboStltRaceGrdCd.index = 0 ;  
            fcSearchRaceRecDetl(); 
            fcInit();

            edAdvRaceNo.text = "" ;  
            edAdvRaceNo_2.text = "" ;  
            edAdvRaceNo_3.text = "" ;  
            //stcRaceLavel.value = cboStltRaceGrdCd.text + " -> " + cboAdvRaceGrdCd.text ; 
            break;

        case "d02000029-service:sesarchRaceRecDetl1" : // 왼쪽 그리드 조회
            // 상단내용 조회 
            fc_titleSearch();    

			if (dsTbdbRaceRecDetl1.rowcount() + dsTbdbRaceRecDetl2.rowcount() +dsTbdbRaceRecDetl3.rowcount() +dsTbdbRaceRecDetl4.rowcount() +dsTbdbRaceRecDetl5.rowcount() +dsTbdbRaceRecDetl6.rowcount() +dsTbdbRaceRecDetl7.rowcount() +dsTbdbRaceRecDetl8.rowcount() > 0 )
			{
				btnCfm.Enable = true; 
			}else{
				btnCfm.Enable = false;
			}

			// 해당되는 선발경주만 남긴다.
			var clearRaceRecDetl1 = true;
			var clearRaceRecDetl2 = true;
			var clearRaceRecDetl3 = true;
			var clearRaceRecDetl4 = true;
			var clearRaceRecDetl5 = true;
			var clearRaceRecDetl6 = true;
			var clearRaceRecDetl7 = true;
			var clearRaceRecDetl8 = true;

			for (var i = 0; i < dsStltRaceGrdCd.rowcount; i++)
			{
				if( dsStltRaceGrdCd.GetColumn(i, "STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl1.GetColumn(0, "RACE_GRD_CD") )
					clearRaceRecDetl1 = false;					
				if( dsStltRaceGrdCd.GetColumn(i, "STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl2.GetColumn(0, "RACE_GRD_CD") )
					clearRaceRecDetl2 = false;				
				if( dsStltRaceGrdCd.GetColumn(i, "STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl3.GetColumn(0, "RACE_GRD_CD") )
					clearRaceRecDetl3 = false;				
				if( dsStltRaceGrdCd.GetColumn(i, "STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl4.GetColumn(0, "RACE_GRD_CD") )
					clearRaceRecDetl4 = false;				
				if( dsStltRaceGrdCd.GetColumn(i, "STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl5.GetColumn(0, "RACE_GRD_CD") )
					clearRaceRecDetl5 = false;				
				if( dsStltRaceGrdCd.GetColumn(i, "STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl6.GetColumn(0, "RACE_GRD_CD") )
					clearRaceRecDetl6 = false;				
				if( dsStltRaceGrdCd.GetColumn(i, "STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl7.GetColumn(0, "RACE_GRD_CD") )
					clearRaceRecDetl7 = false;				
				if( dsStltRaceGrdCd.GetColumn(i, "STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl8.GetColumn(0, "RACE_GRD_CD") )
					clearRaceRecDetl8 = false;
			}

			if( clearRaceRecDetl1 == true )
				dsTbdbRaceRecDetl1.ClearData();
			if( clearRaceRecDetl2 == true )
				dsTbdbRaceRecDetl2.ClearData();
			if( clearRaceRecDetl3 == true )
				dsTbdbRaceRecDetl3.ClearData();
			if( clearRaceRecDetl4 == true )
				dsTbdbRaceRecDetl4.ClearData();
			if( clearRaceRecDetl5 == true )
				dsTbdbRaceRecDetl5.ClearData();
			if( clearRaceRecDetl6 == true )
				dsTbdbRaceRecDetl6.ClearData();
			if( clearRaceRecDetl7 == true )
				dsTbdbRaceRecDetl7.ClearData();
			if( clearRaceRecDetl8 == true )
				dsTbdbRaceRecDetl8.ClearData();

            break; 

		case "d02000029-service:saveTbdbGroupOrg1" : // 저장 완료시
            edAdvRaceNo.text = "" ;  
            edAdvRaceNo_2.text = "" ;  
            edAdvRaceNo_3.text = "" ; 

            break;

        default :
            break;
    }

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

function fcPostProcess(sServiceName) {
} 
//상단의 타이틀 조회  
function fc_titleSearch(obj) {

    //master 조회 
	var sServiceName = "d02000032-service:searchTbdbRaceRecMst001";
    var sInDataSet   = "";
    var sOutDataSet  = "dsTitleSearch=dsTitleSearch ";

	fcd_AddParam(Trim("RACE_YY"), cboYear.value);
	fcd_AddParam(Trim("ROUND"), cboRound.value);
	fcd_AddParam(Trim("RACE_NO"), "1");

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


// 회차 조회 
function cboYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcd_GetRound(strText); 
	cboRound.Index = 0 ; 

	cboMatCdSearch(); //대진표조회 
	fcSearch(obj);
}


// 경주번호 조회 
function cboRound_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	cboMatCdSearch(); //대진표조회 
	fcSearch(obj);
}


// 대진표 조회  
function cboMatCdSearch(){

	var sServiceName = "d02000029-service:searchMatCd";
    var sInDataSet   = "";
    var sOutDataSet  = "dsMatCd=dsMatCd ";

	fcd_AddParam(Trim("RACE_YY"), cboYear.value);
	fcd_AddParam(Trim("ROUND"), cboRound.value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

// 진출경주 조회  
function cboAdvRaceGrdCdSearch(){

	var sServiceName = "d02000029-service:searchAdvRaceGrdCd";
    var sInDataSet   = "";
    var sOutDataSet  = "dsAdvRaceGrdCd=dsAdvRaceGrdCd ";

	fcd_AddParam(Trim("MAT_CD"), cboMatCd.value );  // 대진표 코드 값  

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


// 선발경주 조회  
function cboStltRaceGrdCdSearch(){

	var sServiceName = "d02000029-service:searchStltRaceGrdCd";
    var sInDataSet   = "";
    var sOutDataSet  = "dsStltRaceGrdCd=dsStltRaceGrdCd";

	fcd_AddParam(Trim("MAT_CD"), cboMatCd.value );  				// 대진표 코드 값  
	// 20130519 선발경주 코드값 추가. jdlee
	//fcd_AddParam(Trim("STLT_RACE_GRD_CD"), cboStltRaceGrdCd.value );  // 선발경주 코드 값  
	fcd_AddParam(Trim("ADV_RACE_GRD_CD"), cboAdvRaceGrdCd.value );  // 진출경주 코드 값  

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

