//확인버튼 클릭 시 
function btnCfm_OnClick(obj)
{
	if ( fnc_IsNull(edAdvRaceNo.Text) ) {
		fnc_Message(SNIS_CAN_0008, "N", "진출경주번호");
		return false;
	}


	fcInit(); 		// dataset clear 
	//var jTemp = 0;


	// 선발경주만큼 반복
	for (var StltCnt = 0; StltCnt < dsStltRaceGrdCd.rowcount; StltCnt++)
	{	
		var sStrRand   = toInteger(dsStltRaceGrdCd.GetColumn(StltCnt,"STR_RANK"))-1;
		var sEndRank   = toInteger(dsStltRaceGrdCd.GetColumn(StltCnt,"END_RANK"))-1;
		var sRaceno    = toInteger(dsStltRaceGrdCd.GetColumn(StltCnt,"RACE_NO"));
		var sEquRank   = toInteger(dsStltRaceGrdCd.GetColumn(StltCnt,"EQU_RANK"))-1;
		var sEquRankNo = toInteger(dsStltRaceGrdCd.GetColumn(StltCnt,"EQU_RANK_NO"));
		var sEquRank2  = toInteger(dsStltRaceGrdCd.GetColumn(StltCnt,"EQU_RANK2"))-1;
		var sEquRankNo2= toInteger(dsStltRaceGrdCd.GetColumn(StltCnt,"EQU_RANK_NO2"));
		var sGrdObj    = ""; 
		var j          = 0; 
		var k          = 0; 
		var sTotRow    = 0; 
		var sGrdCnt    = 0;

		dsTempSame.ClearData();
		dsTempSame2.ClearData();

		//////////////////////////////////////////////////////
		// 조편성 데이타 추출 
		//진출코드 (준결승:801 패자전:705)
		var sAdvRaceGrdCd = cboAdvRaceGrdCd.Value; 	

		if (dsTbdbRaceRecDetl1.RowCount() > 0 
			&& dsStltRaceGrdCd.GetColumn(StltCnt,"STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl1.GetColumn(0,"RACE_GRD_CD") ){
			sGrdCnt = 1;
		}else if(dsTbdbRaceRecDetl2.RowCount() > 0 
			&& dsStltRaceGrdCd.GetColumn(StltCnt,"STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl2.GetColumn(0,"RACE_GRD_CD")){
			sGrdCnt = 2;
		}else if(dsTbdbRaceRecDetl3.RowCount() > 0 
			&& dsStltRaceGrdCd.GetColumn(StltCnt,"STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl3.GetColumn(0,"RACE_GRD_CD")){
			sGrdCnt = 3;
		}else if(dsTbdbRaceRecDetl4.RowCount() > 0 
			&& dsStltRaceGrdCd.GetColumn(StltCnt,"STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl4.GetColumn(0,"RACE_GRD_CD")){
			sGrdCnt = 4;
		}else if(dsTbdbRaceRecDetl5.RowCount() > 0 
			&& dsStltRaceGrdCd.GetColumn(StltCnt,"STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl5.GetColumn(0,"RACE_GRD_CD")){
			sGrdCnt = 5;
		}else if(dsTbdbRaceRecDetl6.RowCount() > 0 
			&& dsStltRaceGrdCd.GetColumn(StltCnt,"STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl6.GetColumn(0,"RACE_GRD_CD")){
			sGrdCnt = 6;
		}else if(dsTbdbRaceRecDetl7.RowCount() > 0 
			&& dsStltRaceGrdCd.GetColumn(StltCnt,"STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl7.GetColumn(0,"RACE_GRD_CD")){
			sGrdCnt = 7;
		}else if(dsTbdbRaceRecDetl8.RowCount() > 0 
			&& dsStltRaceGrdCd.GetColumn(StltCnt,"STLT_RACE_GRD_CD") == dsTbdbRaceRecDetl8.GetColumn(0,"RACE_GRD_CD")){
			sGrdCnt = 8;
		}

		// 1~3*2   같은 경우 처리 로직
		for(i=sGrdCnt; i< sGrdCnt+sRaceno; i++) {
			sDsObj = object("dsTbdbRaceRecDetl"+i) ; 
			k = sStrRand ; 

			for(k ; k<=sEndRank; k++){
				if (sDsObj.GetColumn(k,"CAND_NO") == null) break ; 

				dsTemp.AddRow(); 
				dsTemp.SetColumn(dsTemp.currow, "CAND_NO", sDsObj.GetColumn(k,"CAND_NO")); 
				dsTemp.SetColumn(dsTemp.currow, "NM_KOR", sDsObj.GetColumn(k,"NM_KOR")); 
				dsTemp.SetColumn(dsTemp.currow, "TOT_AVG", sDsObj.GetColumn(k,"POINT")); 
				dsTemp.SetColumn(dsTemp.currow, "REAL_TOT_AVG", sDsObj.GetColumn(k,"TOT_AVG")); // 실제 TOT_AVG
				//j = j+1; 
				//jTemp = jTemp +1;
			}
		}

		//j=0; 	

		// 동순위  하위자  편성 (dsTempSame에 나머지 동순위 대상자들을 집어넣는다.)
		for(i=sGrdCnt; i< sGrdCnt+sRaceno; i++) {
			sDsObj = object("dsTbdbRaceRecDetl"+i) ; 

			dsTempSame.AddRow(); 
			dsTempSame.SetColumn(dsTempSame.currow, "CAND_NO", sDsObj.GetColumn(sEquRank,"CAND_NO")); 
			dsTempSame.SetColumn(dsTempSame.currow, "NM_KOR", sDsObj.GetColumn(sEquRank,"NM_KOR")); 
			dsTempSame.SetColumn(dsTempSame.currow, "POINT", sDsObj.GetColumn(sEquRank,"POINT"));
			dsTempSame.SetColumn(dsTempSame.currow, "TOT_AVG", sDsObj.GetColumn(sEquRank,"TOT_AVG")); 	 			
			//j = j+1;
			//jTempSame = jTempSame +1;
		}

		// dsTempSame은 하위자
		dsTempSame.Sort("TOT_AVG",true) ;

		for(k=0 ; k<sEquRankNo; k++){		
			dsTemp.AddRow(); 
			dsTemp.SetColumn(dsTemp.currow, "CAND_NO", dsTempSame.GetColumn(k,"CAND_NO")); 
			dsTemp.SetColumn(dsTemp.currow, "NM_KOR", dsTempSame.GetColumn(k,"NM_KOR")); 
			dsTemp.SetColumn(dsTemp.currow, "TOT_AVG", dsTempSame.GetColumn(k,"POINT")); 
			dsTemp.SetColumn(dsTemp.currow, "REAL_TOT_AVG", dsTempSame.GetColumn(k,"TOT_AVG")); // 실제 TOT_AVG
		}


		//<<<<<<<<<<<<<<<<<동순위 상위자  선수 편성
		//j=0; 

		// 동순위 상위자  선수 편성 (dsTempSame2에 나머지 동순위 대상자들을 집어넣는다.)
		for(i=sGrdCnt; i< sGrdCnt+sRaceno; i++) {
			sDsObj = object("dsTbdbRaceRecDetl"+i) ; 

			dsTempSame2.AddRow(); 
			dsTempSame2.SetColumn(dsTempSame2.currow, "CAND_NO", sDsObj.GetColumn(sEquRank2,"CAND_NO")); 
			dsTempSame2.SetColumn(dsTempSame2.currow, "NM_KOR", sDsObj.GetColumn(sEquRank2,"NM_KOR")); 
			dsTempSame2.SetColumn(dsTempSame2.currow, "POINT", sDsObj.GetColumn(sEquRank2,"POINT"));
			dsTempSame2.SetColumn(dsTempSame2.currow, "TOT_AVG", sDsObj.GetColumn(sEquRank2,"TOT_AVG")); 	 			
			//j = j+1; 
			//jTempSame2 = jTempSame2 + 1;	
		}

		// 상위자
		dsTempSame2.Sort("TOT_AVG",false) ;  

		for(k=0 ; k<sEquRankNo2; k++){		
			dsTemp.AddRow(); 
			dsTemp.SetColumn(dsTemp.currow, "CAND_NO", dsTempSame2.GetColumn(k,"CAND_NO")); 
			dsTemp.SetColumn(dsTemp.currow, "NM_KOR", dsTempSame2.GetColumn(k,"NM_KOR")); 
			dsTemp.SetColumn(dsTemp.currow, "TOT_AVG", dsTempSame2.GetColumn(k,"POINT")); 
			dsTemp.SetColumn(dsTemp.currow, "REAL_TOT_AVG", dsTempSame2.GetColumn(k,"TOT_AVG")); // 실제 TOT_AVG
		}	

		//>>>>>>>>>>>>>>>>>>>동순위 상위자  선수 편성
	}

	//   여기까지 조편성 데이타 추출  
	//////////////////////////////////////////////////////

	//////////////////////////////////////////////////////
	// 여기서부터 각 조로 나눔  (7명씩)

    sTotRow = dsTemp.RowCount(); 

    sGrdCnt = 0 ; 
    //sStartRaceNo = sRaceno-sTotRow/7;	//시작경주번호
    sStartRaceNo = sRaceno-Ceil(sTotRow/7);	//시작경주번호

    // 7보다 크면 (2 경주로 나뉘는 경우, 평균점으로 소팅하여 배정한다. jdlee 20130706
    if( sTotRow > 7 )
    {
		dsTemp.Sort("REAL_TOT_AVG",false) ;  // 실제 평균점수를 갖고 소팅한다.(내림차순)

		dsTemp_1.AddRow(); 
		j=0;
		var advRaceNo = "";

		// 진출경주 수(1 또는 2 또는 3)
		var advRaceCnt = 1;
		if( edAdvRaceNo.Text != "" && edAdvRaceNo_2.Text != "" && edAdvRaceNo_3.Text != "" )
			advRaceCnt = 3;
		else if( edAdvRaceNo_2.Text != "" && edAdvRaceNo_3.Text != "" )
			advRaceCnt = 2;


		for(i=0; i < sTotRow; i++) {
			if( advRaceCnt == 2 )
			{
				// 짝수번째면 
				if (i%2==0) {
					sGrdCnt = 1; 
					j = j+1; 
					advRaceNo = edAdvRaceNo.Text;
				}
				// 홀수번째면
				else
				{
					sGrdCnt = 2; 
					advRaceNo = edAdvRaceNo_2.Text;
				}
			}
			else if( advRaceCnt == 3 )
			{
				if (i%3==0) {
					sGrdCnt = 1; 
					j = j+1; 
					advRaceNo = edAdvRaceNo.Text;
				}
				else if (i%3==1)
				{
					sGrdCnt = 2; 
					advRaceNo = edAdvRaceNo_2.Text;
				}
				else if (i%3==2)
				{
					sGrdCnt = 3; 
					advRaceNo = edAdvRaceNo_3.Text;
				}

			}



			sDsObj = object("dsTbdbGroupOrg"+sGrdCnt) ; 

			sDsObj.AddRow(); 

			//sDsObj.SetColumn(sDsObj.currow, "RACE_NO", ((cboAdvRaceGrdCd.index * sEquRankNo + sGrdCnt)+(cboMatCd.index*dsAdvRaceGrdCd.RowCount()))); 
			//sDsObj.SetColumn(sDsObj.currow, "RACE_NO", ((cboAdvRaceGrdCd.index * sStartRaceNo + sGrdCnt)+(cboMatCd.index*dsAdvRaceGrdCd.RowCount()))); 
			//  경주번호를 직접입력받는다.
			sDsObj.SetColumn(sDsObj.currow, "RACE_NO", advRaceNo); 

			sDsObj.SetColumn(sDsObj.currow, "BACK_NO", j); 

			sDsObj.SetColumn(sDsObj.currow, "RACE_YY", cboYear.value); 
			sDsObj.SetColumn(sDsObj.currow, "ROUND", cboRound.value); 

			sDsObj.SetColumn(sDsObj.currow, "MAT_CD", cboMatCd.value); 
			sDsObj.SetColumn(sDsObj.currow, "RACE_GRD_CD", cboAdvRaceGrdCd.value); 

			sDsObj.SetColumn(sDsObj.currow, "CAND_NO", dsTemp.GetColumn(i,"CAND_NO")); 
			sDsObj.SetColumn(sDsObj.currow, "NM_KOR", dsTemp.GetColumn(i,"NM_KOR")); 
			sDsObj.SetColumn(sDsObj.currow, "TOT_AVG", dsTemp.GetColumn(i,"TOT_AVG")); 

			sDsObj.SetColumn(sDsObj.currow, "CAND_NO", dsTemp2.GetColumn(i,"CAND_NO")); 
			sDsObj.SetColumn(sDsObj.currow, "NM_KOR", dsTemp2.GetColumn(i,"NM_KOR")); 
			sDsObj.SetColumn(sDsObj.currow, "TOT_AVG", dsTemp2.GetColumn(i,"TOT_AVG")); 

			sDsObj.SetColumn(sDsObj.currow, "REAL_TOT_AVG", dsTemp.GetColumn(i,"REAL_TOT_AVG")); 
			//j = j+1; 
		}

	}
	else
	{
		j=0;

		for(i=0; i < sTotRow; i++) {

			if (i%7==0) {
				sGrdCnt = sGrdCnt + 1; 
				j=1; 
			}
			sDsObj = object("dsTbdbGroupOrg"+sGrdCnt) ; 

			sDsObj.AddRow(); 

			//sDsObj.SetColumn(sDsObj.currow, "RACE_NO", ((cboAdvRaceGrdCd.index * sEquRankNo + sGrdCnt)+(cboMatCd.index*dsAdvRaceGrdCd.RowCount()))); 
			//sDsObj.SetColumn(sDsObj.currow, "RACE_NO", ((cboAdvRaceGrdCd.index * sStartRaceNo + sGrdCnt)+(cboMatCd.index*dsAdvRaceGrdCd.RowCount()))); 
			//  경주번호를 직접입력받는다.
			sDsObj.SetColumn(sDsObj.currow, "RACE_NO", edAdvRaceNo.Text); 

			sDsObj.SetColumn(sDsObj.currow, "BACK_NO", j); 

			sDsObj.SetColumn(sDsObj.currow, "RACE_YY", cboYear.value); 
			sDsObj.SetColumn(sDsObj.currow, "ROUND", cboRound.value); 

			sDsObj.SetColumn(sDsObj.currow, "MAT_CD", cboMatCd.value); 
			sDsObj.SetColumn(sDsObj.currow, "RACE_GRD_CD", cboAdvRaceGrdCd.value); 

			sDsObj.SetColumn(sDsObj.currow, "CAND_NO", dsTemp.GetColumn(i,"CAND_NO")); 
			sDsObj.SetColumn(sDsObj.currow, "NM_KOR", dsTemp.GetColumn(i,"NM_KOR")); 
			sDsObj.SetColumn(sDsObj.currow, "TOT_AVG", dsTemp.GetColumn(i,"TOT_AVG")); 

			sDsObj.SetColumn(sDsObj.currow, "CAND_NO", dsTemp2.GetColumn(i,"CAND_NO")); 
			sDsObj.SetColumn(sDsObj.currow, "NM_KOR", dsTemp2.GetColumn(i,"NM_KOR")); 
			sDsObj.SetColumn(sDsObj.currow, "TOT_AVG", dsTemp2.GetColumn(i,"TOT_AVG")); 
			j = j+1; 
		}
	}
//////////////////////////////////////////////////////
}

function fcSave(obj) {

	btnCfm_OnClick(obj);

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";


	// master / detail 삭제 후 저장시킴 
	sServiceName = "d02000029-service:saveTbdbGroupOrg1";	
	sInDataSet  = "dsTbdbGroupOrg1=dsTbdbGroupOrg1:i"
	             + " dsTbdbGroupOrg2=dsTbdbGroupOrg2:i" 
	             + " dsTbdbGroupOrg3=dsTbdbGroupOrg3:i" 
	             + " dsTbdbGroupOrg4=dsTbdbGroupOrg4:i" 
	             + " dsTbdbGroupOrg5=dsTbdbGroupOrg5:i" 
	             + " dsTbdbGroupOrg6=dsTbdbGroupOrg6:i" 
	             + " dsTbdbGroupOrg7=dsTbdbGroupOrg7:i" 
	             + " dsTbdbGroupOrg8=dsTbdbGroupOrg8:i"  ; 
	sOutDataSet  = "dsTbdbGroupOrg1=dsTbdbGroupOrg1"
	             + " dsTbdbGroupOrg2=dsTbdbGroupOrg2" 
	             + " dsTbdbGroupOrg3=dsTbdbGroupOrg3" 
	             + " dsTbdbGroupOrg4=dsTbdbGroupOrg4" 
	             + " dsTbdbGroupOrg5=dsTbdbGroupOrg5" 
	             + " dsTbdbGroupOrg6=dsTbdbGroupOrg6" 
	             + " dsTbdbGroupOrg7=dsTbdbGroupOrg7" 
	             + " dsTbdbGroupOrg8=dsTbdbGroupOrg8"  ; 

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

}
