/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutCUnionGrade))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	
	// 필수항목체크
	oDataSet          = dsOutCUnionGrade;
	sMandatoryColID   = "RACE_YY|GRD";
	sMandatoryColName = "년도|선수등급";
	oGrid             = grdPageList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}
//회차별 구간등록시 check사항
function fcSaveCheck2(obj) {

	if ( fnc_IsNull(cboiStndYear2.Value) ) {
        //조회할 자료가 없음
		fnc_Message(SNIS_CAN_0008, "N", "년도");
		return false;
	}
	if ( fnc_IsNull(edStrRound.Text) ) {
        //조회할 자료가 없음
		fnc_Message(SNIS_CAN_0008, "N", "시작회차");
		return false;
	}
	if ( fnc_IsNull(edStrRound.Text) ) {
        //조회할 자료가 없음
		fnc_Message(SNIS_CAN_0008, "N", "종료회차");
		return false;
	}
	if ( fnc_IsNull(edtGrdRank2.Text) ) {
        //조회할 자료가 없음
		fnc_Message(SNIS_CAN_0008, "N", "등급순위");
		return false;
	}
	if ( fnc_IsNull(cboGrd2.Value) ) {
        //조회할 자료가 없음
		fnc_Message(SNIS_CAN_0008, "N", "선수등급");
		return false;
	}
	if ( fnc_IsNull(edtPropScr2.Text) ) {
        //조회할 자료가 없음
		fnc_Message(SNIS_CAN_0008, "N", "기존선수 고유점수");
		return false;
	}
	if ( fnc_IsNull(edtNfScr2.Text) ) {
        //조회할 자료가 없음
		fnc_Message(SNIS_CAN_0008, "N", "신인선수 고유점수");
		return false;
	}
	
	fcGroupInsert();
	
	return true;
	
}
function fcSave(obj) {

	if(chkOpt.Value == 0){//구간등록이 아닐경우
		
		if ( !fcSaveCheck() ) return;
		
		var sServiceName = "d02000030-service:saveCUnionGrade";
		var sInDataSet   = "dsOutCUnionGrade=dsOutCUnionGrade:u";
		var sOutDataSet  = "dsOutCUnionGrade=dsOutCUnionGrade";		
		
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
		
    }else{				//구간등록일 경우
		if ( !fcSaveCheck2() ) return;
	    
		var sServiceName = "d02000030-service:saveCUnionGrade";
		var sInDataSet   = "dsOutCUnionGrade=dsOutCUnionGrade:u";
		var sOutDataSet  = "dsOutCUnionGrade=dsOutCUnionGrade";	
		
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
		
    }
}
// 회차별 구간등록시 기간 전체를 화면에 뿌려줌
function fcGroupInsert(){

	var iCnt = dsOutCUnionGrade.GetRowCount();		
	var iStr = toNumber(edStrRound.Text);	//시작회차
	var iEnd = toNumber(edEndRound.Text);   //종료회차
	var iCount = 0;
	
	for (var i = iStr; i<= iEnd ;i++){
		dsOutCUnionGrade.AddRow();
		dsOutCUnionGrade.SetColumn(iCnt+iCount,"RACE_YY",cboiStndYear2.Value);
		dsOutCUnionGrade.SetColumn(iCnt+iCount,"Round",i);
		dsOutCUnionGrade.SetColumn(iCnt+iCount,"GRD_RANK",edtGrdRank2.Text);
		dsOutCUnionGrade.SetColumn(iCnt+iCount,"GRD",cboGrd2.Value);
		dsOutCUnionGrade.SetColumn(iCnt+iCount,"PROP_SCR",edtPropScr2.Text);
		dsOutCUnionGrade.SetColumn(iCnt+iCount,"NF_SCR",edtNfScr2.Text);
		iCount++;
	}
}

