/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	// 공통코드 조회 후
	if ( sServiceName == "common-service:searchCode" ) {
		fc_GetRound(cboYear.value); 
		//fcSearch();
	}
	if ( sServiceName == "d02000030-service:searchRound" ) {
		if( dsRound.GetColumn(0, "ROUND") != "")
	{
		dsRound.InsertRow(0);
		dsRound.SetColumn(0, "ROUND", "");
		dsRound.SetColumn(0, "ROUND", ""); 
	}
	
		 cboRound_OnChanged();
    }
	// 조회 후
	if ( sServiceName == "d02000030-service:searchCUnionGrade" ) {
	    if(dsOutCUnionGrade.GetRowCount() > 0){
			fnc_SetStatus(SNIS_CAN_0003, dsOutCUnionGrade.GetRowCount());
	    }
	}
	// 저장 후
	if ( sServiceName == "d02000030-service:saveCUnionGrade" ) {
		if(dsOutCUnionGrade.GetRowCount() > 0){
			fnc_SetStatus(SNIS_CAN_0003, dsOutCUnionGrade.GetRowCount());
	    }
	    fc_GetRound(cboYear.value);
	    fcSearch();
	}
	
	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

