//row 변경시 
function dsOutCUnionGrade_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
	if (dsOutCUnionGrade.GetRowType(nRow) == "insert")
	{
		cboiStndYear.Enable	= true;
		cboiRound.Enable = true;
		cboGrd.Enable 	= true;
	}
	else
	{
		cboiStndYear.Enable	= false;
		cboiRound.Enable = false;
		cboGrd.Enable 	= false;
	}
}


//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutCUnionGrade_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	if(LATEST_ROW_POS > 0) dsOutCUnionGrade.Row = LATEST_ROW_POS;
	dsOutCUnionGrade_OnRowPosChanged(obj, LATEST_ROW_POS, LATEST_ROW_POS);
}

function cboRound_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ChkOpt.Value = 0;
    ChkOpt_OnClick(obj,0);
    var sServiceName = "d02000030-service:searchCUnionGrade";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutCUnionGrade=dsOutCUnionGrade ";
    
    fcd_AddParam("STND_YEAR", cboYear.Value);
	fcd_AddParam(trim("RaceRndNo"), cboRound.Value);
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function cboYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fc_GetRound(strText); 
	cboRound.Index = 0 ; 
	fcSearch();
}

