var sRACE_YY      = ""; 
var sROUND        = ""; 
var sRACE_NO      = ""; 
var sCCIT_CNT     = ""; 



function D02000032_02_OnLoadCompleted(obj)
{
	var		filter_str;

	// 변수가 없으면 그냥 Return
	
	if( IsExistVar("RACE_YY") == false 
   + && IsExistVar("ROUND") == false 
   + && IsExistVar("RACE_NO") == false )   
		return;
	
	sRACE_YY  = RACE_YY;   // 기준년도 
	sROUND    = ROUND;     //  회차
	sRACE_NO  = RACE_NO;   // 경주번호
    sCCIT_CNT = CCIT_CNT;  //주회수
    
    fcd_GetComboDs(dsInComCd);
	fcSearch(this); //조회 


}
