	//화면 종료
	Close();	
}


/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	
    switch(sServiceName) {
    
        // TBDB_VIOL_ACT  유도원 조회 
        case "d02000032-service:searchTbdbInduceDcsn" :
        
			// 표준타임 세팅 
			if ( sCCIT_CNT == '5'){  // 주횟수가 5일 경우 
				meStatTime1 = '40000'; 
				meStatTime2 = '35000'; 
				meStatTime3 = '31000'; 
				meStatTime4 = '29000'; 
				meStatTime5 = '23260'; 
			
			}else if ( sCCIT_CNT == '6'){
				meStatTime1 = '41000'; 
				meStatTime2 = '36000'; 
				meStatTime3 = '33000'; 
				meStatTime4 = '31000'; 
				meStatTime5 = '28000'; 
				meStatTime6 = '23260'; 			
			}else {
			}
				
			if (dsTbdbInduceDcsn.GetCount() == 0){
				dsTbdbInduceDcsn.AddRow();    
				rdGuideCont.Visible = false; 
				rdGuideCont.Value  = ''; 
				rdGuideCont.Enable = false;  
			}else{				
			
				// 체크 해제
				if (dsTbdbInduceDcsn.GetColumn(0,"GUIDE_YN") == '0'){
					rdGuideCont.Visible = false; 
					rdGuideCont.Value  = ''; 
					rdGuideCont.Enable = false; 
					
				//체크상태
				}else if(dsTbdbInduceDcsn.GetColumn(0,"GUIDE_YN") == '1'){
					rdGuideCont.Visible = true; 
					rdGuideCont.Value  = dsTbdbInduceDcsn.GetColumn(0,"GUIDE_CONT"); 
					rdGuideCont.Enable = true; 
				
				}
			
			}
	
            
            break;       
             
        case "d02000032-service:saveTbdbInduceDcsn" :
                        
            break;
        
        default :
            break;
    }
    
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "'";
    

	sServiceName = "d02000032-service:searchTbdbInduceDcsn";
	sInDataSet   = "";
	sOutDataSet  = "dsTbdbInduceDcsn=dsTbdbInduceDcsn";
   
                   
	fcd_AddParam(Trim("RACE_YY"), sRACE_YY);
	fcd_AddParam(Trim("ROUND"),   sROUND);
	fcd_AddParam(Trim("RACE_NO"), sRACE_NO);
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsTbdbInduceDcsn) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}
	
	// 경륜경주기록측정마스타 필수항목체크
	oDataSet          = "dsTbdbInduceDcsn";
	sMandatoryColID   = "RACE_YY|ROUND|RACE_NO|CAND_NO";
	sMandatoryColName = "기준년도|회차|경주번호|후보생번호";
	oGrid             = "";
		
    return true;
}



function btnSave_OnClick(obj) {

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";
    
    if ( !fcSaveCheck(obj) ) return;

	sServiceName = "d02000032-service:saveTbdbInduceDcsn";
	sInDataSet   = "dsTbdbInduceDcsn=dsTbdbInduceDcsn:u";   
	sOutDataSet  = "dsTbdbInduceDcsn=dsTbdbInduceDcsn";
    // fnc_DataSetPrint(dsTbdbInduceDcsn);
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	
}

//사고후 유도여부 체크시 이벤트 
function ckGuideYn_OnClick(obj,strValue)
{
	// 체크 해제
	if (strValue == '0'){
	    rdGuideCont.Visible = false; 
		rdGuideCont.Value  = ''; 
		rdGuideCont.Enable = false; 
		
	//체크상태
	}else if(strValue == '1'){
	    rdGuideCont.Visible = true; 
		rdGuideCont.Value  = dsTbdbInduceDcsn.GetColumn(0,"GUIDE_CONT"); 
		rdGuideCont.Enable = true; 
	
	}
}

