}
//조회 
function fcSearch(obj) {
	fcSearchCheck();
    
    var sServiceName = "d02000034-service:searchCPersonalRecord";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutCPersonalRecord=dsOutCPersonalRecord";
    
    fcd_AddParam("FROM_DT", calFromDt.Value);
    fcd_AddParam("TO_DT", calToDt.Value);
    fcd_AddParam("NM_KOR", edName.Value);
	
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutCPersonalRecord))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
	
	// 필수항목체크
	oDataSet          = dsOutCPersonalRecord;
	sMandatoryColID   = "ESTM_DT|CNT|NM_KOR|CAND_NO";
	sMandatoryColName = "평가일자|회수|선수명|등록번호";
	oGrid             = grdPageList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
	var sServiceName = "d02000034-service:saveCPersonalRecord";
    var sInDataSet   = "dsOutCPersonalRecord=dsOutCPersonalRecord:u";
    var sOutDataSet  = "dsOutCPersonalRecord=dsOutCPersonalRecord";
	
	fcd_AddParam("FROM_DT", calFromDt.Value);
    fcd_AddParam("TO_DT", calToDt.Value);
    fcd_AddParam("NM_KOR", edName.Value);
    trace(dsOutCPersonalRecord.SaveXML());
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

