/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	if( sServiceName == "common-service:searchCode"){
		fcSearchValuation();
	}
	// 조회 후
	if ( sServiceName == "d02000034-service:searchCPersonalRecord" ) {
	    if(dsOutCPersonalRecord.GetRowCount() > 0){
			fnc_SetStatus(SNIS_CAN_0003, dsOutCPersonalRecord.GetRowCount());
	    }
	}
	// 저장 후
	if ( sServiceName == "d02000034-service:saveCPersonalRecord" ) {
		if(dsOutCPersonalRecord.GetRowCount() > 0){
			fnc_SetStatus(SNIS_CAN_0003, dsOutCPersonalRecord.GetRowCount());
	    }
	}
	
	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {
	switch(sServiceName) {
		case "common_help-service:searchRacer" :
			//코드명 조회 후 처리
			fcd_MultiRowDisplay(this, vREVOBJ, sGRDTIT, sCUROBJ);
			var oFocusComponent = GetNextComponent(true);
			oFocusComponent.setfocus();
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  코드나 명 입력 후 Lost Focus 시 해당 정보 Display
+-------------------------------------------------------------------------------------------------*/
function fcCodeNameDisplay(obj) {
	//변화가 없으면 굳이 실행 하지 않는다.
	if (length(obj.userdata) > 0 && obj.value == obj.userdata ) return;
    
	var sInputValue  = trim(obj.value);      //검색 할 자료 

	var sServiceName = "";                   //서버에서 호출 될 Service 명
	var sActionName  = "";                   //서버에서 호출 될 Action 명
	var sInDataSet   = "";                   //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet  = "";                   //조회 후 돌려 받을 DataSet 명칭   
	
	// 초기화
	sCUROBJ  = object(obj.id);
	sGRDTIT  = "";

	switch(obj.id) {
		case "edtNM_KOR" :                   		//선수명
			vREVOBJ[0]   = "edtCAND_NO";			//성명을 받을 Text Object명	
			vREVOBJ[1]   = "edtNM_KOR";				//선수 등록번호를 받을 Text Object명
			sGRDTIT      = "등록번호:성명";			//그리드 제목
				  
			sServiceName = "common_help-service:searchRacer";
			sInDataSet   = "";                      			//자료 저장 시 보낼 DataSet 명칭
			sOutDataSet  = "gdsSelectItem=dsOutRacer";			//조회 후 돌려 받을 DataSet 명칭  
			
			fcd_AddParam(Trim("NM_KOR"), Trim(edtNM_KOR.Text));
			break;
	}
	
	//조회 서비스 실행
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

//row 변경시 
function dsOutCPersonalRecord_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
	if (dsOutCPersonalRecord.GetRowType(nRow) == "insert")
	{
		calESTM_DT.Enable	= true;
		edtCNT.Enable 		= true;
		edtNM_KOR.Enable	= true;
		edtCAND_NO.Enable	= true;
	}
	else
	{
		calESTM_DT.Enable	= false;
		edtCNT.Enable 		= false;
		edtNM_KOR.Enable	= false;
		edtCAND_NO.Enable	= false;
	}
}

//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutCPersonalRecord_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	if(LATEST_ROW_POS > 0) dsOutCPersonalRecord.Row = LATEST_ROW_POS;
	dsOutCPersonalRecord_OnRowPosChanged(obj, LATEST_ROW_POS, LATEST_ROW_POS);
}

function dsOutCPersonalRecord_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var sRec = obj.GetColumn(nRow, strColumnID);
	var sFilterExpr = "FROM_TIME <= " + sRec + " && TO_TIME >= " + sRec;
	
	switch (strColumnID)
	{
		case "TIME_200M":
			dsOutCValuation.Filter("RACELEN = 200 && " + sFilterExpr);
			obj.SetColumn(nRow, "TIME_200M_GRD", dsOutCValuation.GetColumn(0, "GRD"));
			break;
		case "TIME_400M":
			dsOutCValuation.Filter("RACELEN = 400 && " + sFilterExpr);
			obj.SetColumn(nRow, "TIME_400M_GRD", dsOutCValuation.GetColumn(0, "GRD"));
			break;
		case "TIME_1000M":
			dsOutCValuation.Filter("RACELEN = 1000 && " + sFilterExpr);
			obj.SetColumn(nRow, "TIME_1000M_GRD", dsOutCValuation.GetColumn(0, "GRD"));
			break;
	}

}
