/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);
	
	if(fnc_isNull(cboYear.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "년도");
            cboYear.SetFocus();
            return false;
    }
    if(fnc_isNull(cboRoundFrom.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "회차");
            cboRoundFrom.SetFocus();
            return false;
    } 
    if(fnc_isNull(cboRoundTo.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "회차");
            cboRoundTo.SetFocus();
            return false;
    }
    
    
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;
}    


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
      
    if(fnc_isNull(cboYear.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "년도");
            cboYear.SetFocus();
            return false;
    }
    if(fnc_isNull(cboRoundFrom.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "회차");
            cboRoundFrom.SetFocus();
            return false;
    } 
    if(fnc_isNull(cboRoundTo.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "회차");
            cboRoundTo.SetFocus();
            return false;
    }
    
    
	return true;  
     
}

function fcSave(obj) {
	
	if ( !fcSaveCheck() ) return; 
}

