>>  코드나 명 입력 후 Lost Focus 시 해당 정보 Display
+-------------------------------------------------------------------------------------------------*/
//집계자료최초생성
function btnBatch1Save_OnClick(obj)
{
	if ( !fcSaveCheck() ) return; 
	 
	var confirmMsg = Confirm("년도별 집계자료를  최초생성 하시겠습니까?");
	if ( confirmMsg ) {
	    
	    dsOutBatch.ClearData();
	    dsOutBatch.addRow(); 
	    dsOutBatch.SetColumn(0, "RACE_YY",  	 cboYear.Value);
		dsOutBatch.SetColumn(0, "ROUND_FROM",    cboRoundFrom.Value);
 	    dsOutBatch.SetColumn(0, "ROUND_TO",      cboRoundTo.Value);
 	  
 	    var ServiceName = "d02000035-service:saveBatch1";
		var sInDataSet   = "dsOutBatch=dsOutBatch";
		var sOutDataSet  = "dsOutBatch=dsOutBatch";
        
        fcd_SendTransaction(ServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	    
	}
	
}
