function btnBatchTotSave_OnClick(obj)
{
	if ( !fcSaveCheck() ) return; 
	
	var msg = cboRoundFrom.Value+"회차 부터 "+ cboRoundTo.Value+"회차까지 ";
	var confirmMsg = Confirm(msg+" 종합자료를  생성 하시겠습니까?"); 

	if ( confirmMsg ) {
	     
	    dsOutBatch.ClearData();
	    dsOutBatch.addRow();
	    dsOutBatch.SetColumn(0, "RACE_YY",  	 cboYear.Value);
		dsOutBatch.SetColumn(0, "ROUND_FROM",    cboRoundFrom.Value);
